import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 56-265.15:1  Exemptions; routine maintenance.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/56-265.15:1.md","filePath":"vacode/56-265.15:1.md"}'),i={name:"vacode/56-265.15:1.md"},o=n('<h1 id="§-56-265-15-1-exemptions-routine-maintenance" tabindex="-1">§ 56-265.15:1 Exemptions; routine maintenance. <a class="header-anchor" href="#§-56-265-15-1-exemptions-routine-maintenance" aria-label="Permalink to &quot;§ 56-265.15:1  Exemptions; routine maintenance.&quot;">​</a></h1><p>Nothing in this chapter shall apply to:</p><ol><li><p>Any hand digging performed by an owner or occupant of a property.</p></li><li><p>The tilling of soil for agricultural purposes.</p></li><li><p>Any excavation done by a railroad when the excavation is made entirely on the land which the railroad owns and on which the railroad operates, provided there is no encroachment on any operator&#39;s rights-of-way or easements.</p></li><li><p>An excavation or demolition during an emergency, as defined in § <a href="/vacode/56-265.15/">56-265.15</a>, provided all reasonable precaution has been taken to protect the underground utility lines.</p></li></ol><p>In the case of the state highway systems or streets and roads maintained by political subdivisions, officials of the Department of Transportation or the political subdivision where the use of such highways, roads, streets or other public way is impaired by an unforeseen occurrence shall determine the necessity of repair beginning immediately after the occurrence.</p><ol start="5"><li><p>Any excavation for routine pavement maintenance, including patch type paving or the milling of pavement surfaces, upon the paved portion of any street, road, or highway of the Commonwealth provided that any such excavation does not exceed a depth of twelve inches (0.3 meter).</p></li><li><p>Any excavation for the purpose of mining pursuant to and in accordance with the requirements of a permit issued by the Department of Energy.</p></li><li><p>Any hand digging performed by an operator to locate the operator&#39;s utility lines in response to a notice of excavation from the notification center, provided all reasonable precaution has been taken to protect the underground utility lines.</p></li><li><p>Any installation of a sign that does not involve excavation as defined in § <a href="/vacode/56-265.15/">56-265.15</a>.</p></li></ol><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0890" target="_blank" rel="noreferrer">890</a>; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0079" target="_blank" rel="noreferrer">79</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0841" target="_blank" rel="noreferrer">841</a>; 2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0777" target="_blank" rel="noreferrer">777</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0832" target="_blank" rel="noreferrer">832</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0532" target="_blank" rel="noreferrer">532</a>.</p>',6),r=[o];function l(s,p,c,h,d,f){return t(),a("div",null,r)}const m=e(i,[["render",l]]);export{u as __pageData,m as default};
