import{_ as e,o,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 56-265.17:3 Procedures for operators in response to a designer notice.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/56-265.17:3.md","filePath":"vacode/56-265.17:3.md"}'),i={name:"vacode/56-265.17:3.md"},n=r('<h1 id="§-56-265-17-3-procedures-for-operators-in-response-to-a-designer-notice" tabindex="-1">§ 56-265.17:3 Procedures for operators in response to a designer notice. <a class="header-anchor" href="#§-56-265-17-3-procedures-for-operators-in-response-to-a-designer-notice" aria-label="Permalink to &quot;§ 56-265.17:3 Procedures for operators in response to a designer notice.&quot;">​</a></h1><p>An operator, upon notification by a designer in accordance with § <a href="http://law.lis.virginia.gov/vacode/56-265.17:1/" target="_blank" rel="noreferrer">56-265.17:1</a>, shall:</p><ol><li><p>Respond to the designer&#39;s request for underground utility line information within fifteen working days in accordance with subdivisions 2, 3, and 4 of this section;</p></li><li><p>Provide designers with the operator&#39;s name, the type of underground utility line, and the approximate horizontal location of the utility line. The foregoing information may be provided to the designer through the means that include, but are not limited to, field locates, maps, surveys, installation records or other means. If the designer requests field locates, the operator shall provide field locates in accordance with the accuracy set forth in subsection A of § <a href="http://law.lis.virginia.gov/vacode/56-265.19/" target="_blank" rel="noreferrer">56-265.19</a>. Marking shall be done by both paint and flags whenever possible;</p></li><li><p>Provide such information about the location of the utility lines to designers for informational purposes only. Operators will not be liable for any incorrect information provided or for the subsequent use of this information, nor will they be subject to civil penalties for the accuracy of the information or marks provided. Any concerns about the accuracy of information or marks should be directed to the appropriate operator; and</p></li><li><p>Respond to the operator-excavator information exchange system by no later than 7:00 a.m. on the sixteenth working day following the designer&#39;s notice to the notification center.</p></li></ol><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0841" target="_blank" rel="noreferrer">841</a>.</p>',4),a=[n];function s(c,l,d,p,h,f){return o(),t("div",null,a)}const _=e(i,[["render",s]]);export{g as __pageData,_ as default};
