import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 56-265.3:1 Certificates to furnish water and sewer service.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/56-265.3:1.md","filePath":"vacode/56-265.3:1.md"}'),i={name:"vacode/56-265.3:1.md"},o=a('<h1 id="§-56-265-3-1-certificates-to-furnish-water-and-sewer-service" tabindex="-1">§ 56-265.3:1 Certificates to furnish water and sewer service. <a class="header-anchor" href="#§-56-265-3-1-certificates-to-furnish-water-and-sewer-service" aria-label="Permalink to &quot;§ 56-265.3:1 Certificates to furnish water and sewer service.&quot;">​</a></h1><p>A. Any company proposing to construct facilities after January 1, 1995, ultimately intended to make water or sewer service available to more than fifty residential building lots shall, prior to construction or financial commitments therefor, organize a public service corporation and seek certificates of public convenience and necessity pursuant to §§ <a href="http://law.lis.virginia.gov/vacode/56-265.2/" target="_blank" rel="noreferrer">56-265.2</a> and <a href="http://law.lis.virginia.gov/vacode/56-265.3/" target="_blank" rel="noreferrer">56-265.3</a>. The application for such certificates shall include (i) a comprehensive business plan detailing the technical, managerial and financial resources to be devoted to operation of the water or sewer service; (ii) except in the case of a company seeking a certificate to operate a sewer service only, proof of receipt of, or application for, a permit for the facilities pursuant to Virginia Department of Health requirements under Article 2 (§ <a href="http://law.lis.virginia.gov/vacode/32.1-167/" target="_blank" rel="noreferrer">32.1-167</a> et seq.) of Chapter 6 of Title 32.1; and (iii) such other information as is now or hereafter deemed appropriate by the Commission, including proof of the issuance of a bond or the deposit of funds in escrow as may be required by the Department of Health pursuant to § <a href="http://law.lis.virginia.gov/vacode/32.1-174.1/" target="_blank" rel="noreferrer">32.1-174.1</a>.</p><p>B. Certificates of public convenience and necessity shall not be granted by the Commission unless, in addition to the findings required by §§ <a href="http://law.lis.virginia.gov/vacode/56-265.2/" target="_blank" rel="noreferrer">56-265.2</a> and <a href="http://law.lis.virginia.gov/vacode/56-265.3/" target="_blank" rel="noreferrer">56-265.3</a>, it also finds that the comprehensive business plan presented in the application reasonably assures that system performance requirements for providing water supply can be met over the long term and at reasonable costs. The Commission may issue such certification with any conditions or restrictions as public interest may require.</p><p>C. Water or sewer companies existing on or before January 1, 1995 which extend their service at any time following the effective date of this section shall not be required to comply with the requirements thereof.</p><p>D. If any applicant under this section so requests, the Commission shall not disclose the contents of the comprehensive business plan except as necessary to perform its duties.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0852" target="_blank" rel="noreferrer">852</a>.</p>',6),n=[o];function s(c,l,f,h,p,d){return t(),r("div",null,n)}const v=e(i,[["render",s]]);export{m as __pageData,v as default};
