import{_ as o,o as a,c as i,z as e,a as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 56-405.2 Construction and maintenance of crossbucks.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/56-405.2.md","filePath":"vacode/56-405.2.md"}'),s={name:"vacode/56-405.2.md"},n=e("h1",{id:"§-56-405-2-construction-and-maintenance-of-crossbucks",tabindex:"-1"},[t("§ 56-405.2 Construction and maintenance of crossbucks. "),e("a",{class:"header-anchor",href:"#§-56-405-2-construction-and-maintenance-of-crossbucks","aria-label":'Permalink to "§ 56-405.2 Construction and maintenance of crossbucks."'},"​")],-1),r=e("p",null,'Every railroad company shall cause signal boards, hereinafter referred to as crossbucks, well supported by posts or otherwise and approved by the Department of Transportation at such heights as to be easily seen by travelers from both directions of the public highway, and not obstructing travel, containing in capital letters, at least five inches high, the inscription "railroad crossing," to be placed, and constantly maintained, at each public highway at or near, and on both sides of, each place where it is crossed by the railroad at the same level. The requirements of this section in localities that maintain their own streets may be waived at specific crossings on the petition of any such company to both the Commissioner of Highways and the public road authority if both the Commissioner and the public road authority determine that any such crossing has or will have other adequate warning devices or that the placement of new crossbucks will not enhance the safety of the traveling public. Neither official action nor failure to act as hereinabove provided shall impair the power of the Commissioner or the public road authority to require crossbucks at specific public crossings should a subsequent determination of their need be made.',-1),c=e("p",null,"The cost of erecting crossbucks placed at a public highway for the first time or whenever the Commissioner or the public road authority determines an upgrade of the standards is required may be paid or supplemented from federal funds when available to the Department of Transportation for such purpose at the sole discretion of the Commissioner of Highways. But the election of the Commissioner not to participate in such cost shall not relieve any company from the obligation of this section.",-1),h=e("p",null,"This section shall apply as to cities and towns in the case of new crossbucks beginning July 1, 1977.",-1),l=e("p",null,[t("1977, c. 226; 1996, cc. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0114",target:"_blank",rel:"noreferrer"},"114"),t(", "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0157",target:"_blank",rel:"noreferrer"},"157"),t(".")],-1),d=[n,r,c,h,l];function p(u,f,m,b,g,y){return a(),i("div",null,d)}const w=o(s,[["render",p]]);export{v as __pageData,w as default};
