import{_ as t,o as n,c as s,z as e,a as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 56-414 Bell and whistle or horn; when sounded.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/56-414.md","filePath":"vacode/56-414.md"}'),a={name:"vacode/56-414.md"},i=e("h1",{id:"§-56-414-bell-and-whistle-or-horn-when-sounded",tabindex:"-1"},[o("§ 56-414 Bell and whistle or horn; when sounded. "),e("a",{class:"header-anchor",href:"#§-56-414-bell-and-whistle-or-horn-when-sounded","aria-label":'Permalink to "§ 56-414 Bell and whistle or horn; when sounded."'},"​")],-1),r=e("p",null,"Every railroad company shall provide each locomotive passing upon its road with a bell of ordinary size and steam whistle or horn, and such whistle or horn shall be sharply sounded outside cities and towns at least twice at a distance of not less than 300 yards nor more than 600 yards from the place where the railroad crosses upon the same level any public highway or crossing, and such bell shall be rung or whistle or horn sounded continuously or alternately until the locomotive has reached such highway crossing, and shall give such signals in cities and towns as their local governing bodies may require.",-1),l=e("p",null,"The governing body of any county, city, or town may by ordinance require locomotives to sound their whistle upon approaching designated railroad trestles or bridges having lengths of 100 feet or more. Notice of any such requirement shall be given by registered mail to the registered agent of the railroad operating in the affected county, city, or town. Affected railroads shall comply with any such ordinance within 30 days of receiving the notice.",-1),h=e("p",null,"The governing body of any county, city, or town may, by ordinance adopted following a public hearing, petition the State Corporation Commission to enter an order, pursuant to the Commission's Rules of Practice and Procedure, requiring locomotives to sound their whistle or horn at specifically identified private crossings in the same manner as required for public crossings. If the Commission should deem the blowing of the locomotive whistle at such private crossings to be necessary in the interest of safety under all relevant circumstances, then it shall enter an order. The affected railroad shall comply with the order within 90 days of receipt by its registered agent of notice sent by registered mail and the locality must first install stop signs on both sides of such private crossing, to be paid for by the locality or the landowner. The Commission may establish and collect a fee, not to exceed its actual costs, from applicants for an order to sound locomotive whistles pursuant to this section.",-1),d=e("p",null,[o("Code 1919, § 3958; 1950, p. 944; 1956, c. 164; 1993, c. 483; 2003, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0287",target:"_blank",rel:"noreferrer"},"287"),o(".")],-1),c=[i,r,l,h,d];function u(p,f,m,g,y,w){return n(),s("div",null,c)}const _=t(a,[["render",u]]);export{v as __pageData,_ as default};
