import{_ as e,o,c as t,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 56-419.2 Safety requirements applicable to vehicles transporting railroad employees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/56-419.2.md","filePath":"vacode/56-419.2.md"}'),r={name:"vacode/56-419.2.md"},i=a('<h1 id="§-56-419-2-safety-requirements-applicable-to-vehicles-transporting-railroad-employees" tabindex="-1">§ 56-419.2 Safety requirements applicable to vehicles transporting railroad employees. <a class="header-anchor" href="#§-56-419-2-safety-requirements-applicable-to-vehicles-transporting-railroad-employees" aria-label="Permalink to &quot;§ 56-419.2 Safety requirements applicable to vehicles transporting railroad employees.&quot;">​</a></h1><p>A. As used in this section &quot;motor vehicle&quot; shall mean any motor vehicle designed for highway use, owned or operated by a railroad, whether or not it is used on the highways of this Commonwealth.</p><p>B. No motor vehicle shall be used for transporting one or more railroad employees three miles or more to or from a work situs unless such motor vehicle is constructed and maintained so as to provide safe transportation for such employees.</p><p>C. The requirement of safe transportation as set out hereinabove shall include, but not be limited to, the construction and maintenance of motor vehicles so as:</p><ol><li><p>To provide an enclosure providing full cover from the elements for all railroad employees being so transported. Such enclosure shall be heated.</p></li><li><p>To provide within said enclosure fixed seats with backs for all railroad employees being so transported.</p></li><li><p>To provide a means to effectively communicate to the driver of the motor vehicle the emergency needs of the railroad employees being so transported.</p></li></ol><p>D. The provisions of this section shall not apply to any motor vehicle when an emergency arises and such vehicle must be used to meet such an emergency.</p><p>E. The failure of any railroad company to correct any violation of this section within seven days from receipt of written notice thereof shall subject said company to the penalty provided by § <a href="http://law.lis.virginia.gov/vacode/56-449/" target="_blank" rel="noreferrer">56-449</a>; provided, however, any unsafe vehicle shall be removed immediately from service until repaired.</p><p>1977, c. 628.</p>',8),s=[i];function n(l,c,p,h,d,m){return o(),t("div",null,s)}const v=e(r,[["render",n]]);export{u as __pageData,v as default};
