import{_ as r,o,c as t,z as e,a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 56-436 Board of appraisers to appraise injured or killed livestock; duty of appraisers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/56-436.md","filePath":"vacode/56-436.md"}'),s={name:"vacode/56-436.md"},i=e("h1",{id:"§-56-436-board-of-appraisers-to-appraise-injured-or-killed-livestock-duty-of-appraisers",tabindex:"-1"},[a("§ 56-436 Board of appraisers to appraise injured or killed livestock; duty of appraisers. "),e("a",{class:"header-anchor",href:"#§-56-436-board-of-appraisers-to-appraise-injured-or-killed-livestock-duty-of-appraisers","aria-label":'Permalink to "§ 56-436 Board of appraisers to appraise injured or killed livestock; duty of appraisers."'},"​")],-1),n=e("p",null,"Whenever any horses, cattle, or other livestock are killed or injured, or other property damaged, by the cars or locomotives upon any railroad, it shall be lawful for the owner thereof or for the railroad company to have the property examined and the damages assessed by a board of appraisers in the following manner:",-1),l=e("p",null,"Either party, his agent or attorney, may appoint one person as the appraiser in his behalf, and notify the other party; such notice, when intended for the railroad company, shall be sufficient if given by certified mail to the registered agent of such railroad. Then the party so notified shall appoint an appraiser on his behalf, and the two appraisers shall select a third appraiser. These three persons shall constitute a board of appraisers to examine and appraise the property so injured or damaged, and shall examine the horses or other livestock so killed, or injured, or the other property so damaged, and affix a value upon the same if killed, or assess the damages to the same if injured, and make a written report, carefully describing the horses, cattle, or other livestock or property, stating whether killed or injured, and also setting out the valuation or assessment of damages made by them. Such report shall be returned to the office of the clerk of the circuit court of the county or city in which such livestock was killed or injured, who shall file and preserve the same.",-1),d=e("p",null,[a("Code 1919, § 3994; 1994, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0352",target:"_blank",rel:"noreferrer"},"352"),a(".")],-1),p=[i,n,l,d];function h(c,f,u,m,y,k){return o(),t("div",null,p)}const g=r(s,[["render",h]]);export{_ as __pageData,g as default};
