import{_ as o,o as t,c as a,z as e,a as s}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 56-466 Location of posts, poles, cables and conduits; height of wires, etc.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/56-466.md","filePath":"vacode/56-466.md"}'),r={name:"vacode/56-466.md"},n=e("h1",{id:"§-56-466-location-of-posts-poles-cables-and-conduits-height-of-wires-etc",tabindex:"-1"},[s("§ 56-466 Location of posts, poles, cables and conduits; height of wires, etc. "),e("a",{class:"header-anchor",href:"#§-56-466-location-of-posts-poles-cables-and-conduits-height-of-wires-etc","aria-label":'Permalink to "§ 56-466 Location of posts, poles, cables and conduits; height of wires, etc."'},"​")],-1),i=e("p",null,"All posts, poles, wires, cables and conduits which shall be erected by any authority in the preceding sections of this chapter conferred shall be so located as in no way to obstruct or interfere with public travel or the ordinary use of, or the safety and convenience of persons traveling through, on, or over, the public parks, roads, turnpikes, streets, avenues, alleys, railroads, or waters in or upon which the same may be erected, and all wires fastened upon posts or poles erected as aforesaid shall be placed at a height of not less than eighteen feet above all road crossings, and twenty-three feet above railroad crossings, and no conduits shall be laid nor posts or poles erected upon the soil or property of any person without first obtaining the consent of the owner thereof, nor shall any such wires or cables be strung across the soil, property, or premises of any person, or attached to or connected with any shade or ornamental tree, or any private building, without the consent of the owner thereof.",-1),c=e("p",null,"Such poles, wires, cables and conduits shall not in anywise damage private property without compensation therefor, nor in any way obstruct the navigation of any stream, or impair or endanger the use thereof by the public, or by any person or corporation entitled to the use of the same. Such conduits shall be laid at such distance below the surface of any public park, road, turnpike, street, avenue, or alley, and at such distance from the outside of any gas or water main or other conduit already laid under such public park, road, turnpike, street, avenue, or alley, as may be prescribed by the proper municipal, county, or state authorities.",-1),l=e("p",null,"Code 1919, §§ 4035, 4038; 1926, pp. 908, 909; 1976, c. 268.",-1),h=[n,i,c,l];function d(p,u,f,b,y,w){return t(),a("div",null,h)}const g=o(r,[["render",d]]);export{_ as __pageData,g as default};
