import{_ as e,o as i,c as t,O as a}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 56-479.2 Anti-competitive acts; injunctive relief.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/56-479.2.md","filePath":"vacode/56-479.2.md"}'),r={name:"vacode/56-479.2.md"},n=a('<h1 id="§-56-479-2-anti-competitive-acts-injunctive-relief" tabindex="-1">§ 56-479.2 Anti-competitive acts; injunctive relief. <a class="header-anchor" href="#§-56-479-2-anti-competitive-acts-injunctive-relief" aria-label="Permalink to &quot;§ 56-479.2 Anti-competitive acts; injunctive relief.&quot;">​</a></h1><p>A. No telecommunications service provider shall engage in anti-competitive acts or practices in connection with its provision of telecommunications services including price discrimination, predatory pricing or tying arrangements, as such terms are commonly applied in antitrust law.</p><p>B. Any telecommunications service provider injured or threatened with injury by a violation of any of the provisions of this section or § <a href="http://law.lis.virginia.gov/vacode/15.2-2160/" target="_blank" rel="noreferrer">15.2-2160</a> may maintain a cause of action for injunctive relief, damages, or both, and for reasonable costs and attorney&#39;s fees before the circuit court for the locality in which the injury occurs.</p><p>2002, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0479" target="_blank" rel="noreferrer">479</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0489" target="_blank" rel="noreferrer">489</a>.</p>',4),o=[n];function c(s,l,p,f,v,_){return i(),t("div",null,o)}const m=e(r,[["render",c]]);export{h as __pageData,m as default};
