import{_ as t,o as r,c as i,z as e,a as o}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 56-493 Membership; voting; nonprofit operation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/56-493.md","filePath":"vacode/56-493.md"}'),a={name:"vacode/56-493.md"},n=e("h1",{id:"§-56-493-membership-voting-nonprofit-operation",tabindex:"-1"},[o("§ 56-493 Membership; voting; nonprofit operation. "),e("a",{class:"header-anchor",href:"#§-56-493-membership-voting-nonprofit-operation","aria-label":'Permalink to "§ 56-493 Membership; voting; nonprofit operation."'},"​")],-1),s=e("p",null,"A cooperative may issue to its members certificates of membership. Only members shall be entitled to vote at the meetings of the members of the cooperative. A member entitled to vote may vote in person or, unless the articles of incorporation or the bylaws otherwise provide, may vote by proxy executed in writing by the member, appointing some other member to cast his vote, or may vote by his duly authorized attorney-in-fact, who shall be a member. If a member is not voting in person, by proxy, or by attorney-in-fact, the spouse of such member shall be entitled to vote for the member, in person or by proxy, if permitted by the articles of incorporation or bylaws. No proxy shall be valid after eleven months from its date unless otherwise provided in the proxy. When directors or officers are to be elected by members, the bylaws may provide that such elections may be conducted by mail. The liability of each member shall be limited to the unpaid portion of his membership fee or subscription to capital stock, and any unpaid bills for telephone service from the cooperative. The equity of members of a nonstock cooperative shall be in proportion to the revenue paid the cooperative by each member. A cooperative shall be operated on a nonprofit basis for the mutual benefit of its members. The bylaws of the cooperative or its contract with its members shall contain such provisions relative to the disposition of revenues and receipts as may be necessary and appropriate to establish and maintain its nonprofit and cooperative character.",-1),p=e("p",null,[o("1950, p. 591; 1956, c. 434; 1968, c. 100; 1992, c. 6; 1996, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0708",target:"_blank",rel:"noreferrer"},"708"),o(".")],-1),c=[n,s,p];function h(m,l,b,d,f,v){return r(),i("div",null,c)}const _=t(a,[["render",h]]);export{u as __pageData,_ as default};
