import{_ as i,o as n,c as a,z as e,a as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 56-509 Uninterrupted functioning and operation of essential public utilities.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/56-509.md","filePath":"vacode/56-509.md"}'),o={name:"vacode/56-509.md"},r=e("h1",{id:"§-56-509-uninterrupted-functioning-and-operation-of-essential-public-utilities",tabindex:"-1"},[t("§ 56-509 Uninterrupted functioning and operation of essential public utilities. "),e("a",{class:"header-anchor",href:"#§-56-509-uninterrupted-functioning-and-operation-of-essential-public-utilities","aria-label":'Permalink to "§ 56-509 Uninterrupted functioning and operation of essential public utilities."'},"​")],-1),s=e("p",null,"The continuous, uninterrupted, and proper functioning and operation of public utilities engaged in the business of furnishing water, light, heat, gas, electric power, transportation, communication, or any one or more of them to the people of Virginia are hereby declared to be essential to the public welfare, health, and safety. It is contrary to the public policy of the Commonwealth to permit any substantial impairment or suspension of the operation of any such utility, and it is the duty of the Commonwealth to exercise all available means and every power at its command to prevent the same so as to protect its citizens from any dangers, perils, calamities, or catastrophes that would result therefrom. Further, such utilities are declared to be of vital public interest, and as such, it is necessary that impairment or suspension of the operation of any such utility for any reason be prevented to the extent and by the means hereafter provided.",-1),l=e("p",null,[t("1952, c. 696; 2022, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0234",target:"_blank",rel:"noreferrer"},"234"),t(".")],-1),c=[r,s,l];function p(u,d,h,f,m,b){return n(),a("div",null,c)}const y=i(o,[["render",p]]);ex