import{_ as t,o as i,c as n,z as e,a}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 56-551 Termination of certificate; dedication of assets.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/56-551.md","filePath":"vacode/56-551.md"}'),o={name:"vacode/56-551.md"},r=e("h1",{id:"§-56-551-termination-of-certificate-dedication-of-assets",tabindex:"-1"},[a("§ 56-551 Termination of certificate; dedication of assets. "),e("a",{class:"header-anchor",href:"#§-56-551-termination-of-certificate-dedication-of-assets","aria-label":'Permalink to "§ 56-551 Termination of certificate; dedication of assets."'},"​")],-1),s=e("p",null,"Within ninety days of the completion and closing of the original permanent financing, the operator shall provide full details of the financing, including the terms of all bonds, to the Commission; and shall certify the term of the original permanent financing and its termination date. The Commission may require that the operator provide copies of any relevant documents, and shall review the financing and determine the date of termination of the original permanent financing. After establishing this date, the Commission shall enter an order terminating the operator's authority pursuant to the certificate of authority on a date which shall be ten years from the end of the term of the original permanent financing. At the request of the operator or the Department, or on its own initiative, the Commission may revise its order to modify the date for termination of the certificate of authority in order to take into account any refinancing of the original permanent financing, where the refinancing or modification is in the public interest, or any refinancing for the purpose of expansion, or early termination of the original permanent financing. Upon the termination of the certificate of authority, the authority and duties of the operator under this chapter shall cease, and the highway assets and improvements of the operator shall be dedicated to the Commonwealth for highway purposes.",-1),h=e("p",null,"1988, c. 649; 1991, c. 272.",-1),c=[r,s,h];function f(d,m,l,p,u,g){return i(),n("div",null,c)}const v=t(o,[["render",f]]);export{y as __pageData,v as default};
