import{_ as o,o as i,c as n,z as e,a as t}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 56-575.13 Utility crossing.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/56-575.13.md","filePath":"vacode/56-575.13.md"}'),r={name:"vacode/56-575.13.md"},a=e("h1",{id:"§-56-575-13-utility-crossing",tabindex:"-1"},[t("§ 56-575.13 Utility crossing. "),e("a",{class:"header-anchor",href:"#§-56-575-13-utility-crossing","aria-label":'Permalink to "§ 56-575.13 Utility crossing."'},"​")],-1),s=e("p",null,"The private entity and each public service company, public utility, railroad, and cable television provider, whose facilities are to be crossed or affected shall cooperate fully with the other entity in planning and arranging the manner of the crossing or relocation of the facilities. Any such entity possessing the power of condemnation is hereby expressly granted such powers in connection with the moving or relocation of facilities to be crossed by the qualifying project or that must be relocated to the extent that such moving or relocation is made necessary or desirable by construction of, renovation to, or improvements to the qualifying project, which shall be construed to include construction of, renovation to, or improvements to temporary facilities for the purpose of providing service during the period of construction or improvement. Any amount to be paid for such crossing, construction, moving or relocating of facilities shall be paid for by the private entity. Should the private entity and any such public service company, public utility, railroad, and cable television provider not be able to agree upon a plan for the crossing or relocation, the Commission may determine the manner in which the crossing or relocation is to be accomplished and any damages due arising out of the crossing or relocation. The Commission may employ expert engineers who shall examine the location and plans for such crossing or relocation, hear any objections and consider modifications, and make a recommendation to the Commission. In such a case, the cost of the experts is to be borne by the private entity. Such determination shall be made by the Commission within ninety days of notification by the private entity that the qualifying project will cross utilities subject to the Commission's jurisdiction.",-1),c=e("p",null,[t("2002, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0571",target:"_blank",rel:"noreferrer"},"571"),t("; 2005, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0865",target:"_blank",rel:"noreferrer"},"865"),t(".")],-1),l=[a,s,c];function h(d,p,m,u,f,g){return i(),n("div",null,l)}const v=o(r,[["render",h]]);export{b as __pageData,v as default};
