import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 56-584 Stranded costs.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/56-584.md","filePath":"vacode/56-584.md"}'),o={name:"vacode/56-584.md"},i=r('<h1 id="§-56-584-stranded-costs" tabindex="-1">§ 56-584 Stranded costs. <a class="header-anchor" href="#§-56-584-stranded-costs" aria-label="Permalink to &quot;§ 56-584 Stranded costs.&quot;">​</a></h1><p>Just and reasonable net stranded costs, to the extent that they exceed zero value in total for the incumbent electric utility, shall be recoverable by each incumbent electric utility provided each incumbent electric utility shall only recover its just and reasonable net stranded costs through either capped rates as provided in § <a href="http://law.lis.virginia.gov/vacode/56-582/" target="_blank" rel="noreferrer">56-582</a>. To the extent not preempted by federal law, the establishment by the Commission of wires charges for any distribution cooperative shall be conditioned upon such cooperative entering into binding commitments by which it will pay to any power supply cooperative of which such distribution cooperative is or was a member, as compensation for such power supply cooperative&#39;s stranded costs, all or part of the proceeds of such wires charges, as determined by the Commission.</p><p>1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0411" target="_blank" rel="noreferrer">411</a>; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0888" target="_blank" rel="noreferrer">888</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0933" target="_blank" rel="noreferrer">933</a>.</p>',3),s=[i];function n(c,l,d,h,p,_){return t(),a("div",null,s)}const b=e(o,[["render",n]]);export{f as __pageData,b as default};
