import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 56-592.1 Consumer education program; scope and funding.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/56-592.1.md","filePath":"vacode/56-592.1.md"}'),o={name:"vacode/56-592.1.md"},r=n('<h1 id="§-56-592-1-consumer-education-program-scope-and-funding" tabindex="-1">§ 56-592.1 Consumer education program; scope and funding. <a class="header-anchor" href="#§-56-592-1-consumer-education-program-scope-and-funding" aria-label="Permalink to &quot;§ 56-592.1 Consumer education program; scope and funding.&quot;">​</a></h1><p>A. The Commission shall establish and implement the consumer education program developed pursuant to subsection A of § <a href="http://law.lis.virginia.gov/vacode/56-592/" target="_blank" rel="noreferrer">56-592</a>. In establishing such a program, the Commission shall take into account the findings and recommendations of the subgroup on Information/Consumer Education that was established in conjunction with the Commission&#39;s proceeding in Case PUE-2007-00049, that implemented the third enactment of Chapters 888 and 933 of the Acts of Assembly of 2007.</p><p>B. The program shall be designed to (i) enable consumers to make rational and informed choices about the matters described in subsection A of § <a href="http://law.lis.virginia.gov/vacode/56-592/" target="_blank" rel="noreferrer">56-592</a>, including but not limited to demand side management, energy conservation, and energy efficiency, (ii) help consumers reduce transaction costs in making decisions regarding such matters, and (iii) foster compliance with the consumer protection provisions of this chapter.</p><p>C. The Commission shall regularly consult with representatives of consumer organizations, community-based groups, state agencies, incumbent utilities, and other interested parties throughout the program&#39;s implementation and operation.</p><p>D. Pursuant to the provisions of § <a href="http://law.lis.virginia.gov/vacode/30-205/" target="_blank" rel="noreferrer">30-205</a>, the Commission shall provide periodic updates to the Commission on Electric Utility Regulation concerning the program&#39;s implementation and operation.</p><p>E. The Commission shall fund the establishment and operation of such consumer education program through the special regulatory revenue tax currently authorized by § <a href="http://law.lis.virginia.gov/vacode/58.1-2660/" target="_blank" rel="noreferrer">58.1-2660</a> and the special regulatory tax authorized by Chapter 29 (§ <a href="http://law.lis.virginia.gov/vacode/58.1-2900/" target="_blank" rel="noreferrer">58.1-2900</a> et seq.) of Title 58.1.</p><p>2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0991" target="_blank" rel="noreferrer">991</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0885" target="_blank" rel="noreferrer">885</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0883" target="_blank" rel="noreferrer">883</a>.</p>',7),i=[r];function s(c,l,d,h,p,m){return t(),a("div",null,i)}const f=e(o,[["render",s]]);export{g as __pageData,f as default};
