import{_ as o,o as t,c as i,z as e,a as s}from"./chunks/framework.9482e208.js";const w=JSON.parse('{"title":"§ 56-73 Penalty as to officers, etc., for violation of provisions of chapter.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/56-73.md","filePath":"vacode/56-73.md"}'),r={name:"vacode/56-73.md"},n=e("h1",{id:"§-56-73-penalty-as-to-officers-etc-for-violation-of-provisions-of-chapter",tabindex:"-1"},[s("§ 56-73 Penalty as to officers, etc., for violation of provisions of chapter. "),e("a",{class:"header-anchor",href:"#§-56-73-penalty-as-to-officers-etc-for-violation-of-provisions-of-chapter","aria-label":'Permalink to "§ 56-73 Penalty as to officers, etc., for violation of provisions of chapter."'},"​")],-1),a=e("p",null,"Every officer, agent or employee of a public service company, and every other person (1) who knowingly authorizes, directs, aids in, issues or executes, or causes to be issued or executed, any stock or stock certificate or other evidence of interest or ownership, or bond, note or other evidence of indebtedness, otherwise than in conformity with the order of the Commission authorizing the same, or contrary to the provisions of this chapter; or (2) who, in any proceedings before the Commission, knowingly makes any false statement or representation or, with knowledge of its falsity, files or causes to be filed with the Commission any false statement or representation which statement or representation so made, filed or caused to be filed may tend in any way to influence the Commission to make an order authorizing the issuance of any stock or stock certificate or other evidence of interest or ownership, or any bond, note, or other evidence of indebtedness, or which results in procuring from the Commission the making of any such order; or (3) who, with knowledge that any false statement or representation was made to the Commission in any proceedings tending in any way to influence the Commission to make such order, issues or executes or originally negotiates, or causes to be issued, executed or originally negotiated any such stock or stock certificate or other evidence of interest or ownership, or bond, note or other evidence of indebtedness; or (4) who, directly or indirectly, knowingly applies, or causes or assists to be applied the proceeds, or any part thereof, from the sale of any stock or stock certificate or other evidence of interest or ownership, or bond, note or other evidence of indebtedness, to any purpose not specified in the Commission's order or to any purpose specified in the Commission's order in excess of the amount authorized for such purpose; or (5) who, with knowledge that any stock or stock certificate or other evidence of interest or ownership, or bond, note or other evidence of indebtedness, has been issued or executed in violation of any of the provisions of this chapter, originally negotiates the same, or causes the same to be originally negotiated, shall be guilty of a misdemeanor.",-1),c=e("p",null,"1934, p. 225; Michie Code 1942, § 4073(13).",-1),h=[n,a,c];function d(f,l,p,m,y,u){return t(),i("div",null,h)}const g=o(r,[["render",d]]);export{w as __pageData,g as default};
