import{_ as t,o as r,c as a,z as e,a as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 57-14 Suits by members to have land sold or mortgaged.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/57-14.md","filePath":"vacode/57-14.md"}'),s={name:"vacode/57-14.md"},h=e("h1",{id:"§-57-14-suits-by-members-to-have-land-sold-or-mortgaged",tabindex:"-1"},[o("§ 57-14 Suits by members to have land sold or mortgaged. "),e("a",{class:"header-anchor",href:"#§-57-14-suits-by-members-to-have-land-sold-or-mortgaged","aria-label":'Permalink to "§ 57-14 Suits by members to have land sold or mortgaged."'},"​")],-1),i=e("p",null,"Whenever the governing body of any church diocese or any religious congregation whose property is held by trustees, for whose use a conveyance, devise, or dedication of land has been lawfully made, shall deem that their interest will be promoted by a sale of the whole or a part of such land, or by a mortgage thereof, or deed of trust thereon, it shall be lawful for any member of such church diocese or congregation, in his name and on behalf of the other members thereof, to prosecute a suit for either of such purposes in the circuit court of the county, or circuit court of the city, in which such land or the greater part thereof lies, against the trustees or the survivors of them in whom the legal title is; and it shall be lawful for such court, if a proper case be made, and it appears that the governing body of the church diocese or the congregation has given its assent thereto in the mode prescribed by its authorities, and the court be of opinion that the rights of others will not be violated thereby, to order the sale of such land or part thereof, or the execution of such mortgage or deed of trust, and make such disposition of the proceeds of such sale as the governing body of the church diocese or congregation may desire.",-1),n=e("p",null,[o("Code 1919, § 45; 1962, c. 516; 2005, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0772",target:"_blank",rel:"noreferrer"},"772"),o(".")],-1),c=[h,i,n];function d(l,u,f,m,g,b){return r(),a("div",null,c)}const y=t(s,[["render",d]]);export{v as __pageData,y as default};
