import{_ as t,o,c as s,z as e,a}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 57-37 Costs of suits, removal and reinterment; how surplus above costs disposed of.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/57-37.md","filePath":"vacode/57-37.md"}'),n={name:"vacode/57-37.md"},r=e("h1",{id:"§-57-37-costs-of-suits-removal-and-reinterment-how-surplus-above-costs-disposed-of",tabindex:"-1"},[a("§ 57-37 Costs of suits, removal and reinterment; how surplus above costs disposed of. "),e("a",{class:"header-anchor",href:"#§-57-37-costs-of-suits-removal-and-reinterment-how-surplus-above-costs-disposed-of","aria-label":'Permalink to "§ 57-37 Costs of suits, removal and reinterment; how surplus above costs disposed of."'},"​")],-1),i=e("p",null,"In the event that the proceeds from the condemnation are insufficient to defray the costs of the suit and removal, and reinterment of the remains, then the additional amount necessary therefor shall be paid by the county, city or town instituting the suit. If, after the payment of proper costs, there be any residue, the same shall be paid to the parties entitled thereto, and if there be any parties unknown who are entitled to such proceeds, the county, city or town shall hold such amount in trust for the parties so entitled, but such county, city or town shall not be charged with any interest on such amount so held. If the amount so held is not claimed by or paid to the parties entitled thereto within seven years from the date of sale of such burying ground, or part thereof, then such amount shall pass to and become a part of the Literary Fund of the Commonwealth, as provided by the statute of escheats.",-1),d=e("p",null,"Code 1919, § 55; 1985, c. 95.",-1),h=[r,i,d];function c(l,u,f,p,m,_){return o(),s("div",null,h)}const y=t(n,[["render",c]]);export{b as __pageData,y as default};
