import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 57-39.7 Applicability; abandonment determination limited in certain circumstances.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/57-39.7.md","filePath":"vacode/57-39.7.md"}'),i={name:"vacode/57-39.7.md"},o=n('<h1 id="§-57-39-7-applicability-abandonment-determination-limited-in-certain-circumstances" tabindex="-1">§ 57-39.7 Applicability; abandonment determination limited in certain circumstances. <a class="header-anchor" href="#§-57-39-7-applicability-abandonment-determination-limited-in-certain-circumstances" aria-label="Permalink to &quot;§ 57-39.7 Applicability; abandonment determination limited in certain circumstances.&quot;">​</a></h1><p>Sections <a href="/vacode/57-39.2/">57-39.2</a> through <a href="/vacode/57-39.6/">57-39.6</a> shall be construed to apply to and authorize a determination of abandonment of any unoccupied part of a cemetery lot. In any proceeding to determine the abandonment of an unoccupied part of a cemetery lot, the court shall also determine what part, if any, shall be considered as having been abandoned. Such sections shall not be construed to apply to and authorize a determination of abandonment of (i) that part of a cemetery lot wherein there has been an interment, (ii) any cemetery lot or part thereof to which unrestricted fee simple title has been conveyed by a cemetery, or (iii) any cemetery lot or part thereof for which perpetual care has been provided by contract with the city, town, corporation, association, or trustees having ownership and charge of the cemetery containing any such lot or part thereof.</p><p>1962, c. 264; 1964, c. 111; 1985, c. 414; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0669" target="_blank" rel="noreferrer">669</a>.</p>',3),r=[o];function c(d,s,l,h,p,m){return t(),a("div",null,r)}const u=e(i,[["render",c]]);export{f as __pageData,u as default};
