import{_ as e,o,c as t,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 57-55.4 Collection receptacles; required disclosures.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/57-55.4.md","filePath":"vacode/57-55.4.md"}'),i={name:"vacode/57-55.4.md"},r=a('<h1 id="§-57-55-4-collection-receptacles-required-disclosures" tabindex="-1">§ 57-55.4 Collection receptacles; required disclosures. <a class="header-anchor" href="#§-57-55-4-collection-receptacles-required-disclosures" aria-label="Permalink to &quot;§ 57-55.4 Collection receptacles; required disclosures.&quot;">​</a></h1><p>No (i) business as defined in § <a href="http://law.lis.virginia.gov/vacode/58.1-3700.1/" target="_blank" rel="noreferrer">58.1-3700.1</a>, other than a charitable or civic organization to which contributions are deductible under § 170 of the Internal Revenue Code, or (ii) professional solicitor shall place or maintain a receptacle in public view for the purpose of collecting donated clothing, household items, or other items for future resale unless such person or professional solicitor places on the receptacle a permanent sign or label in a prominent place that includes the following information printed in letters that are no less than three inches in height and no less than one-half inch in width and in a color that contrasts with the color of the receptacle, which sign or label shall be placed immediately below the opening in the receptacle used to deposit donations:</p><ol><li><p>The name, business address, and telephone number of the person engaged in such business or the professional solicitor; and</p></li><li><p>a. For a receptacle placed or maintained by a person engaged in such business, a statement that reads: &quot;This donation receptacle is operated by a for-profit business. Items donated here support a for-profit business.&quot;; or</p></li></ol><p>b. For a receptacle placed or maintained by the professional solicitor, a statement that reads: &quot;This donation receptacle is operated by a professional solicitor. Items donated here support, in part, the professional solicitor, which is a for-profit entity.&quot;</p><p>Enforcement of a violation of this section shall be limited to subsections B through E of § <a href="http://law.lis.virginia.gov/vacode/57-59/" target="_blank" rel="noreferrer">57-59</a>.</p><p>2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0476" target="_blank" rel="noreferrer">476</a>.</p>',6),s=[r];function n(l,c,p,h,d,u){return o(),t("div",null,s)}const b=e(i,[["render",n]]);export{_ as __pageData,b as default};
