import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 58.1-1003.3 Roll-your-own cigarette machines.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-1003.3.md","filePath":"vacode/58.1-1003.3.md"}'),o={name:"vacode/58.1-1003.3.md"},i=r('<h1 id="§-58-1-1003-3-roll-your-own-cigarette-machines" tabindex="-1">§ 58.1-1003.3 Roll-your-own cigarette machines. <a class="header-anchor" href="#§-58-1-1003-3-roll-your-own-cigarette-machines" aria-label="Permalink to &quot;§ 58.1-1003.3 Roll-your-own cigarette machines.&quot;">​</a></h1><p>Any person who maintains, operates, or rents a machine at a retail establishment for use by a consumer that enables any person to process at the establishment a product that is made or derived from tobacco into a roll or tube shall be deemed to be a manufacturer of cigarettes, and the resulting product produced at such establishment shall be deemed to be manufactured cigarettes sold to a consumer for purposes of this title, Chapter 42 (§ <a href="http://law.lis.virginia.gov/vacode/3.2-4200/" target="_blank" rel="noreferrer">3.2-4200</a> et seq.) of Title 3.2, and Chapter 2.1 (§ <a href="http://law.lis.virginia.gov/vacode/9.1-209/" target="_blank" rel="noreferrer">9.1-209</a> et seq.) of Title 9.1. A retail establishment may purchase tobacco that has not been subject to tax pursuant to this title or the requirements of Chapter 42 of Title 3.2, provided that (i) such tobacco may only be sold to consumers for the purpose of making cigarettes on the machines described herein in the establishment, (ii) the retail establishment pays the taxes due on such cigarettes pursuant to this title, and (iii) the retail establishment maintains compliance with the requirements of Chapter 42 of Title 3.2 with respect to such cigarettes. The provisions of this section shall not apply to the sale and use of cigarette rolling machines purchased for personal use by an individual consumer to make cigarettes for personal consumption and not for rental or use by other consumers.</p><p>2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0048" target="_blank" rel="noreferrer">48</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0068" target="_blank" rel="noreferrer">68</a>; 2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0370" target="_blank" rel="noreferrer">370</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0418" target="_blank" rel="noreferrer">418</a>.</p>',3),s=[i];function n(l,c,h,p,u,d){return t(),a("div",null,s)}const g=e(o,[["render",n]]);export{f as __pageData,g as default};
