import{_ as t,o,c as e,O as a}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 58.1-1021.04:3 Unlawful importation, transportation, or possession of tobacco products; civil penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-1021.04:3.md","filePath":"vacode/58.1-1021.04:3.md"}'),r={name:"vacode/58.1-1021.04:3.md"},i=a('<h1 id="§-58-1-1021-04-3-unlawful-importation-transportation-or-possession-of-tobacco-products-civil-penalty" tabindex="-1">§ 58.1-1021.04:3 Unlawful importation, transportation, or possession of tobacco products; civil penalty. <a class="header-anchor" href="#§-58-1-1021-04-3-unlawful-importation-transportation-or-possession-of-tobacco-products-civil-penalty" aria-label="Permalink to &quot;§ 58.1-1021.04:3 Unlawful importation, transportation, or possession of tobacco products; civil penalty.&quot;">​</a></h1><p>A. It shall be unlawful for any person who is not a licensed distributor in the Commonwealth pursuant to this article to import, transport, or possess, for resale, any tobacco products in the Commonwealth, or under circumstances and conditions that indicate that tobacco products are being imported, transported, or possessed in a manner as to knowingly and intentionally evade or attempt to evade the tax imposed by this article. Such tobacco products shall be subject to seizure, forfeiture, and destruction by any law-enforcement officer of the Commonwealth. All fixtures, equipment, materials, and personal property used in substantial connection with the sale or possession of tobacco products involved in a knowing and intentional violation of this article shall be subject to seizure and forfeiture according to the procedures contained in Chapter 22.1 (§ <a href="http://law.lis.virginia.gov/vacode/19.2-386.1/" target="_blank" rel="noreferrer">19.2-386.1</a> et seq.) of Title 19.2, applied mutatis mutandis.</p><p>B. Any person, except as otherwise provided by law, who imports, transports, or possesses for resale tobacco products upon which the tax imposed by this article has not been paid shall be required to pay any tax owed pursuant to this article. In addition, if such person imports, transports, or possesses such tobacco products in such a manner as to knowingly and intentionally evade or attempt to evade the tax imposed by this article, he shall be required to pay a civil penalty of (i) $2.50 per tobacco product, up to $500, for the first violation by the person within a 36-month period; (ii) $5 per tobacco product, up to $1,000, for the second violation by the person within a 36-month period; and (iii) $10 per tobacco product, up to $50,000, for the third or subsequent violation by the person within a 36-month period, to be assessed and collected by the Department as other taxes are collected. In addition, where willful intent exists to defraud the Commonwealth of the tax levied under this article, such person shall be required to pay a civil penalty of $25 per tobacco product, up to $250,000.</p><p>2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0071" target="_blank" rel="noreferrer">71</a>; 2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0038" target="_blank" rel="noreferrer">38</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0177" target="_blank" rel="noreferrer">177</a>.</p>',4),n=[i];function s(c,p,l,d,h,u){return o(),e("div",null,n)}const m=t(r,[["render",s]]);export{b as __pageData,m as default};
