import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 58.1-1031 Definitions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-1031.md","filePath":"vacode/58.1-1031.md"}'),n={name:"vacode/58.1-1031.md"},o=i('<h1 id="§-58-1-1031-definitions" tabindex="-1">§ 58.1-1031 Definitions. <a class="header-anchor" href="#§-58-1-1031-definitions" aria-label="Permalink to &quot;§ 58.1-1031 Definitions.&quot;">​</a></h1><p>As used in this chapter, unless the context requires a different meaning:</p><p>&quot;Cigarette&quot; means any product that contains nicotine, is intended to be burned or heated under ordinary conditions of use, and consists of or contains (i) any roll of tobacco wrapped in paper or in any substance not containing tobacco; (ii) tobacco, in any form, that is functional in the product, which, because of its appearance, the type of tobacco used in the filler, or its packaging and labeling, is likely to be offered to, or purchased by, consumers as a cigarette; or (iii) any roll of tobacco wrapped in any substance containing tobacco which, because of its appearance, the type of tobacco used in the filler, or its packaging and labeling, is likely to be offered to, or purchased by, consumers as a cigarette described in clause (i) of this definition.</p><p>&quot;Department&quot; means the Department of Taxation.</p><p>&quot;Importer&quot; means the same as that term is defined in 26 U.S.C. § 5702 (k).</p><p>&quot;Package&quot; means the same as that term is defined in 15 U.S.C. § 1332 (4).</p><p>2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0880" target="_blank" rel="noreferrer">880</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0901" target="_blank" rel="noreferrer">901</a>.</p>',7),r=[o];function s(c,d,p,f,l,h){return t(),a("div",null,r)}const b=e(n,[["render",s]]);export{_ as __pageData,b as default};
