import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 58.1-1035 Revocation or suspension of permit by Department; civil penalties; sharing of information.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-1035.md","filePath":"vacode/58.1-1035.md"}'),r={name:"vacode/58.1-1035.md"},n=o('<h1 id="§-58-1-1035-revocation-or-suspension-of-permit-by-department-civil-penalties-sharing-of-information" tabindex="-1">§ 58.1-1035 Revocation or suspension of permit by Department; civil penalties; sharing of information. <a class="header-anchor" href="#§-58-1-1035-revocation-or-suspension-of-permit-by-department-civil-penalties-sharing-of-information" aria-label="Permalink to &quot;§ 58.1-1035 Revocation or suspension of permit by Department; civil penalties; sharing of information.&quot;">​</a></h1><p>A. The Department may revoke or suspend the permit of any wholesale dealer, as defined in § <a href="http://law.lis.virginia.gov/vacode/58.1-1000/" target="_blank" rel="noreferrer">58.1-1000</a>, for a violation of this chapter or any rule adopted by the Department as provided in § <a href="http://law.lis.virginia.gov/vacode/58.1-1011/" target="_blank" rel="noreferrer">58.1-1011</a>.</p><p>B. In addition, the Department may impose a civil penalty in an amount not to exceed the greater of 500 percent of the retail value of the cigarettes involved or $5,000 upon finding a violation of this chapter and may assess the tax due and any interest on the product acquired, possessed, sold, or offered for sale in violation of this chapter.</p><p>C. For the purpose of enforcing this chapter, the Department may request or share information with any federal, state or local agency, including any agency of another state or local agency thereof.</p><p>2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0880" target="_blank" rel="noreferrer">880</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0901" target="_blank" rel="noreferrer">901</a>.</p>',5),i=[n];function s(p,l,c,f,h,d){return a(),t("div",null,i)}const _=e(r,[["render",s]]);export{v as __pageData,_ as default};
