import{_ as a,o,c as r,z as e,a as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 58.1-1117 How intangible personal property tax collectible.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-1117.md","filePath":"vacode/58.1-1117.md"}'),l={name:"vacode/58.1-1117.md"},n=e("h1",{id:"§-58-1-1117-how-intangible-personal-property-tax-collectible",tabindex:"-1"},[t("§ 58.1-1117 How intangible personal property tax collectible. "),e("a",{class:"header-anchor",href:"#§-58-1-1117-how-intangible-personal-property-tax-collectible","aria-label":'Permalink to "§ 58.1-1117 How intangible personal property tax collectible."'},"​")],-1),s=e("p",null,"Each county and city treasurer shall proceed promptly to collect all intangible personal property taxes for the tax year that have been assessed by the commissioner of the revenue and remain unpaid after the time fixed by law for payment and shall continue his efforts so to collect until the close of the then current calendar year. The collection of such taxes shall be enforced by legal process to the extent collection cannot be accomplished otherwise, and all remedies available to the county or city treasurer for the collection of other taxes shall apply to the collection of intangible personal property taxes.",-1),i=e("p",null,[t("Within thirty-one days after the close of each calendar year, the treasurer shall transmit to the Department in the form it may prescribe, such information as the Department may require with respect to all assessments that the commissioner of the revenue made during such calendar year and that the treasurer was unable to collect. The Department, upon receiving and examining the same, shall certify to the Comptroller the necessary information to enable the Comptroller to give such treasurer proper credit on the Comptroller's books for all unpaid items, and such treasurer shall not receive any of such taxes after he has transmitted such information to the Department, but the same shall be paid directly into the state treasury. Section "),e("a",{href:"http://law.lis.virginia.gov/vacode/58.1-1800/",target:"_blank",rel:"noreferrer"},"58.1-1800"),t(" shall not apply with respect to the intangible property taxes covered by this paragraph.")],-1),c=e("p",null,[t("The Department shall have power to issue a memorandum of lien under § "),e("a",{href:"http://law.lis.virginia.gov/vacode/58.1-1805/",target:"_blank",rel:"noreferrer"},"58.1-1805"),t(" for the collection of such taxes in the same manner and with the same effect as in the case of a memorandum of lien issued for the collection of taxes assessed by such Department; and all provisions of law applicable to such memorandum of lien shall be applicable to each memorandum of lien issued for the collection of taxes under this section. The Department shall also have power to collect the taxes as aforesaid by other legal process.")],-1),h=e("p",null,"Code 1950, § 58-441; 1960, c. 508; 1977, c. 396; 1984, c. 675; 1985, c. 221.",-1),p=[n,s,i,c,h];function d(m,f,u,b,y,_){return o(),r("div",null,p)}const v=a(l,[["render",d]]);export{g as __pageData,v as default};
