import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 58.1-1405 Time for payment of tax.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-1405.md","filePath":"vacode/58.1-1405.md"}'),r={name:"vacode/58.1-1405.md"},i=o('<h1 id="§-58-1-1405-time-for-payment-of-tax" tabindex="-1">§ 58.1-1405 Time for payment of tax. <a class="header-anchor" href="#§-58-1-1405-time-for-payment-of-tax" aria-label="Permalink to &quot;§ 58.1-1405 Time for payment of tax.&quot;">​</a></h1><p>A. Except as provided in paragraph B of this section, the tax levied pursuant to this chapter shall be paid by the purchaser or user of such watercraft and collected by the Tax Commissioner at the time the owner is required to apply to the Department of Wildlife Resources for a title. Except as otherwise provided in § <a href="/vacode/58.1-1404/">58.1-1404</a>, no title shall be issued unless the applicant for title shows to the satisfaction of the Department of Wildlife Resources that such tax has been paid.</p><p>B. The tax on the gross receipts from the lease or charter of watercraft shall be paid by the registered dealer collecting such receipts to the Commissioner on or before the twentieth day of each month following the month in which such receipts were collected.</p><p>Code 1950, §§ 58-685.44, 58-685.46; 1981, c. 405; 1984, c. 675; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0877" target="_blank" rel="noreferrer">877</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0958" target="_blank" rel="noreferrer">958</a>.</p>',4),s=[i];function c(n,h,l,p,f,d){return t(),a("div",null,s)}const u=e(r,[["render",c]]);export{m as __pageData,u as default};
