import{_ as a,o as t,c as o,z as e,a as n}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 58.1-1717 Undervaluation of estate; collection of additional tax; minimum additional tax or refund payable.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-1717.md","filePath":"vacode/58.1-1717.md"}'),r={name:"vacode/58.1-1717.md"},i=e("h1",{id:"§-58-1-1717-undervaluation-of-estate-collection-of-additional-tax-minimum-additional-tax-or-refund-payable",tabindex:"-1"},[n("§ 58.1-1717 Undervaluation of estate; collection of additional tax; minimum additional tax or refund payable. "),e("a",{class:"header-anchor",href:"#§-58-1-1717-undervaluation-of-estate-collection-of-additional-tax-minimum-additional-tax-or-refund-payable","aria-label":'Permalink to "§ 58.1-1717 Undervaluation of estate; collection of additional tax; minimum additional tax or refund payable."'},"​")],-1),d=e("p",null,"The clerk of the court wherein the probate or administration tax has been paid by an estate shall thereafter compare the total value of the probate estate as shown on the probate tax return with the total value shown on the inventory of such estate to determine whether the estate has been undervalued for tax purposes. If such clerk finds that such estate has been undervalued, he shall thereupon collect such additional tax as may be due. In the event of an overpayment of such tax, the personal representative may apply to the Department of Taxation and, if a local probate tax was paid, to the treasurer of the city or county for a refund. No additional tax shall be payable or no refund made if the payment or refund due would be less than twenty-five dollars.",-1),l=e("p",null,"Code 1950, § 58-70; 1973, c. 446; 1978, c. 838; 1983, c. 140; 1984, c. 675; 1989, c. 223.",-1),s=[i,d,l];function c(h,u,f,p,m,x){return t(),o("div",null,s)}const v=a(r,[["render",c]]);export{b as __pageData,v as default};
