import{_ as e,o as r,c as a,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 58.1-1737 Exemptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-1737.md","filePath":"vacode/58.1-1737.md"}'),t={name:"vacode/58.1-1737.md"},o=i('<h1 id="§-58-1-1737-exemptions" tabindex="-1">§ 58.1-1737 Exemptions. <a class="header-anchor" href="#§-58-1-1737-exemptions" aria-label="Permalink to &quot;§ 58.1-1737 Exemptions.&quot;">​</a></h1><p>No tax shall be imposed as provided in § <a href="http://law.lis.virginia.gov/vacode/58.1-1736/" target="_blank" rel="noreferrer">58.1-1736</a> if the vehicle is:</p><ol><li><p>Rented by the United States government or any governmental agency thereof;</p></li><li><p>Rented by the Commonwealth of Virginia or any political subdivision thereof;</p></li><li><p>A self-contained mobile computerized axial tomography scanner rented by a nonprofit hospital or a cooperative hospital service organization as described in § 501(e) of the Internal Revenue Code;</p></li><li><p>A self-contained mobile unit designed exclusively for human diagnostic or therapeutic service, rented to a nonprofit hospital or a cooperative hospital service organization as described in § 501(e) of the Internal Revenue Code, or a nonprofit corporation as defined in § 501(c)(3) of the Internal Revenue Code, established for research in, diagnosis of, or therapy for human ailments; or</p></li><li><p>A truck, tractor truck, trailer, or semitrailer, as severally defined in § <a href="http://law.lis.virginia.gov/vacode/46.2-100/" target="_blank" rel="noreferrer">46.2-100</a>, except trailers and semitrailers not designed or used to carry property and vehicles registered under § <a href="http://law.lis.virginia.gov/vacode/46.2-700/" target="_blank" rel="noreferrer">46.2-700</a>, with a gross vehicle weight rating or gross combination weight rating of 26,001 pounds or more, in which case no tax shall be imposed pursuant to subdivision A 1 of § <a href="http://law.lis.virginia.gov/vacode/58.1-1736/" target="_blank" rel="noreferrer">58.1-1736</a>.</p></li></ol><p>2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0405" target="_blank" rel="noreferrer">405</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0639" target="_blank" rel="noreferrer">639</a>.</p>',4),n=[o];function s(l,c,d,p,h,f){return r(),a("div",null,n)}const _=e(t,[["render",s]]);export{v as __pageData,_ as default};
