import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 58.1-1744 (For contingent expiration, see Acts 2020, cc. 1230 and 1275) Local transportation transient occupancy tax.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-1744.md","filePath":"vacode/58.1-1744.md"}'),o={name:"vacode/58.1-1744.md"},r=n('<h1 id="§-58-1-1744-for-contingent-expiration-see-acts-2020-cc-1230-and-1275-local-transportation-transient-occupancy-tax" tabindex="-1">§ 58.1-1744 (For contingent expiration, see Acts 2020, cc. 1230 and 1275) Local transportation transient occupancy tax. <a class="header-anchor" href="#§-58-1-1744-for-contingent-expiration-see-acts-2020-cc-1230-and-1275-local-transportation-transient-occupancy-tax" aria-label="Permalink to &quot;§ 58.1-1744 (For contingent expiration, see Acts 2020, cc. 1230 and 1275) Local transportation transient occupancy tax.&quot;">​</a></h1><p>In addition to all other fees and taxes imposed under law, there is hereby imposed an additional transient occupancy tax at the rate of three percent of the amount of the charge for the occupancy of any room or space occupied in any county or city that is (i) a member of the Northern Virginia Transportation Authority and (ii) that is not described in § <a href="/vacode/58.1-1743/">58.1-1743</a>.</p><p>The tax imposed under this section shall be imposed only for the occupancy of any room or space that is suitable or intended for occupancy by transients for dwelling, lodging, or sleeping purposes.</p><p>The tax imposed under this section shall be administered by the locality in which the room or space is located in the same manner as it administers the tax authorized by § <a href="/vacode/58.1-3819/">58.1-3819</a> or <a href="/vacode/58.1-3840/">58.1-3840</a>, mutatis mutandis, except as herein provided. The revenue generated and collected from the tax shall be deposited by the local treasurer. Two-thirds of the revenue collected pursuant to this section may be used only for public transportation purposes and the remaining revenue may be used for any transportation purpose.</p><p>2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0854" target="_blank" rel="noreferrer">854</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0856" target="_blank" rel="noreferrer">856</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1230" target="_blank" rel="noreferrer">1230</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1275" target="_blank" rel="noreferrer">1275</a>.</p>',5),i=[r];function c(s,p,l,d,h,u){return t(),a("div",null,i)}const _=e(o,[["render",c]]);export{g as __pageData,_ as default};
