import{_ as t,o,c as i,z as e,a as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 58.1-1809 Jurisdiction over Commonwealth for purpose of determining validity, amount and priority of tax lien.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-1809.md","filePath":"vacode/58.1-1809.md"}'),a={name:"vacode/58.1-1809.md"},n=e("h1",{id:"§-58-1-1809-jurisdiction-over-commonwealth-for-purpose-of-determining-validity-amount-and-priority-of-tax-lien",tabindex:"-1"},[r("§ 58.1-1809 Jurisdiction over Commonwealth for purpose of determining validity, amount and priority of tax lien. "),e("a",{class:"header-anchor",href:"#§-58-1-1809-jurisdiction-over-commonwealth-for-purpose-of-determining-validity-amount-and-priority-of-tax-lien","aria-label":'Permalink to "§ 58.1-1809 Jurisdiction over Commonwealth for purpose of determining validity, amount and priority of tax lien."'},"​")],-1),s=e("p",null,"Any court having jurisdiction over a creditor's bill, partition suit, condemnation suit, interpleader or other cause or action in which it is necessary to make the Commonwealth a party in order to determine the respective rights of two or more other adverse parties, shall have jurisdiction over the Commonwealth for the limited purposes of determining the validity of a tax lien of the Commonwealth, the amount of such lien, and the priority of such lien vis-a-vis other liens. Such court shall have no jurisdiction to determine the validity of the assessment secured by the lien. This section shall apply only if the pleadings clearly set forth the nature of the tax lien and service is properly made upon the Attorney General.",-1),d=e("p",null,"Code 1950, § 58-1010.1; 1972, c. 478; 1984, c. 675.",-1),l=[n,s,d];function c(h,m,p,u,f,v){return o(),i("div",null,l)}const x=t(a,[["render",c]]);export{_ as __pageData,x as default};
