import{_ as t,o as r,c as o,z as e,a}from"./chunks/framework.9482e208.js";const T=JSON.parse('{"title":"§ 58.1-1818 Taxpayer problem resolution program; taxpayer assistance orders.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-1818.md","filePath":"vacode/58.1-1818.md"}'),s={name:"vacode/58.1-1818.md"},i=e("h1",{id:"§-58-1-1818-taxpayer-problem-resolution-program-taxpayer-assistance-orders",tabindex:"-1"},[a("§ 58.1-1818 Taxpayer problem resolution program; taxpayer assistance orders. "),e("a",{class:"header-anchor",href:"#§-58-1-1818-taxpayer-problem-resolution-program-taxpayer-assistance-orders","aria-label":'Permalink to "§ 58.1-1818 Taxpayer problem resolution program; taxpayer assistance orders."'},"​")],-1),n=e("p",null,"A taxpayer problem resolution program shall be available to taxpayers to facilitate the prompt review and resolution of taxpayer complaints and problems which have not been addressed or remedied through normal administrative proceedings or operational procedures and to assure that taxpayer rights are safeguarded and protected during the tax determination and collection processes.",-1),d=e("p",null,"The Tax Commissioner shall designate a taxpayers' rights advocate and adequate staff to administer the taxpayer problem resolution program.",-1),l=e("p",null,"The taxpayers' rights advocate may issue a taxpayer assistance order that suspends or stays actions or proposed actions by the Department when a taxpayer suffers or is about to suffer a significant hardship as a result of a tax determination, collection, or enforcement process. When determined to be necessary by the taxpayers' rights advocate, he may require a formal written request to be submitted by the taxpayer.",-1),p=e("p",null,"Relief or remedy may be granted by a taxpayer assistance order only as an extraordinary measure. The process shall not be used to contest the merits of a tax liability, or as a substitute for informal protest procedures, or normal administrative or judicial proceedings for the review of a tax assessment or collection action, or denial of refund.",-1),c=e("p",null,"The running of the period of limitations on an assessment shall be tolled from the date of a taxpayer's request for a taxpayer assistance order until either the date the request is denied or the date specified in the taxpayer assistance order, whichever is applicable.",-1),h=e("p",null,[a("1996, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0634",target:"_blank",rel:"noreferrer"},"634"),a(".")],-1),m=[i,n,d,l,p,c,h];function u(f,y,x,_,b,g){return r(),o("div",null,m)}const w=t(s,[["render",u]]);export{T as __pageData,w as default};
