import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 58.1-1900 Classification of employees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-1900.md","filePath":"vacode/58.1-1900.md"}'),o={name:"vacode/58.1-1900.md"},r=i('<h1 id="§-58-1-1900-classification-of-employees" tabindex="-1">§ 58.1-1900 Classification of employees. <a class="header-anchor" href="#§-58-1-1900-classification-of-employees" aria-label="Permalink to &quot;§ 58.1-1900 Classification of employees.&quot;">​</a></h1><p>A. For the purposes of this title and Title 40.1, Title 60.2, and Title 65.2, if an individual performs services for an employer for remuneration, that individual shall be considered an employee of the party that pays that remuneration unless such individual or his employer demonstrates that such individual is an independent contractor. The Department shall determine whether an individual is an independent contractor by applying Internal Revenue Service guidelines.</p><p>B. Unless otherwise provided in this chapter, the Department shall administer this chapter according to the provisions of Article 16 (§ <a href="/vacode/58.1-460/">58.1-460</a> et seq.) of Chapter 3, mutatis mutandis.</p><p>C. For the purposes of this chapter, all occurrences of misclassification of employees as described hereinafter made by the same employer at the same time, or within 72 hours, shall be deemed to be a single offense.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0681" target="_blank" rel="noreferrer">681</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0682" target="_blank" rel="noreferrer">682</a>.</p>',5),s=[r];function n(l,c,p,d,h,f){return a(),t("div",null,s)}const u=e(o,[["render",n]]);export{_ as __pageData,u as default};
