import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 58.1-1901 Civil penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-1901.md","filePath":"vacode/58.1-1901.md"}'),o={name:"vacode/58.1-1901.md"},r=i('<h1 id="§-58-1-1901-civil-penalties" tabindex="-1">§ 58.1-1901 Civil penalties. <a class="header-anchor" href="#§-58-1-1901-civil-penalties" aria-label="Permalink to &quot;§ 58.1-1901 Civil penalties.&quot;">​</a></h1><p>Any employer, or any officer or agent of the employer, that fails to properly classify an individual as an employee in accordance with § <a href="/vacode/58.1-1900/">58.1-1900</a> for purposes of this title, Title 40.1, Title 60.2, or Title 65.2 and fails to pay taxes, benefits, or other contributions required to be paid with respect to an employee shall, upon notice by the Department to the affected party, be subject to a civil penalty of up to $1,000 per misclassified individual for a first offense, up to $2,500 per misclassified individual for a second offense, and up to $5,000 per misclassified individual for a third or subsequent offense. Each civil penalty assessed under this chapter shall be paid into the general fund.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0681" target="_blank" rel="noreferrer">681</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0682" target="_blank" rel="noreferrer">682</a>.</p>',3),s=[r];function n(l,c,p,d,f,h){return a(),t("div",null,s)}const v=e(o,[["render",n]]);export{u as __pageData,v as default};
