import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 58.1-1902 Debarment; civil penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-1902.md","filePath":"vacode/58.1-1902.md"}'),r={name:"vacode/58.1-1902.md"},n=o('<h1 id="§-58-1-1902-debarment-civil-penalty" tabindex="-1">§ 58.1-1902 Debarment; civil penalty. <a class="header-anchor" href="#§-58-1-1902-debarment-civil-penalty" aria-label="Permalink to &quot;§ 58.1-1902 Debarment; civil penalty.&quot;">​</a></h1><p>A. Whenever the Department determines, after notice to the employer, that an employer failed to properly classify an individual as an employee under the provisions of § <a href="/vacode/58.1-1900/">58.1-1900</a>, the Department shall notify all public bodies and covered institutions of the name of the employer.</p><p>B. Upon an employer&#39;s subsequent violations of subsection A, all public bodies and covered institutions shall not award a contract to such employer or to any firm, corporation, or partnership in which the employer has an interest in the following manner:</p><ol><li><p>For a period of up to one year, as determined by the Department, from the date of the notice for a second offense.</p></li><li><p>For a period of up to three years, as determined by the Department, from the date of the notice for a third or subsequent offense.</p></li></ol><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0681" target="_blank" rel="noreferrer">681</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0682" target="_blank" rel="noreferrer">682</a>.</p>',5),i=[n];function l(s,p,c,d,h,f){return t(),a("div",null,i)}const u=e(r,[["render",l]]);export{_ as __pageData,u as default};
