import{_ as t,o as i,c as o,z as e,a}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 58.1-2031 Hearing; notice.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-2031.md","filePath":"vacode/58.1-2031.md"}'),n={name:"vacode/58.1-2031.md"},r=e("h1",{id:"§-58-1-2031-hearing-notice",tabindex:"-1"},[a("§ 58.1-2031 Hearing; notice. "),e("a",{class:"header-anchor",href:"#§-58-1-2031-hearing-notice","aria-label":'Permalink to "§ 58.1-2031 Hearing; notice."'},"​")],-1),s=e("p",null,"As soon as practicable after the filing of the petition, the State Corporation Commission shall set a date for the hearing. At least fifteen days' notice of such hearing shall be given by the Commission to the petitioner and to every party in interest, but such notice to the petitioner shall be sufficient in the form of an attested copy of the order fixing such hearing sent by ordinary mail, to the address furnished by the petitioner, or to the attorney of the petitioner, if any, provided it is certified in the record, by the clerk of the Commission, that such notice was deposited in the mail at least fifteen days prior to the date set for the hearing. Otherwise such notice may be served by an officer or may be served by registered mail, return receipt requested, and, except in the case of the petitioner shall be accompanied by a copy of the petition. The petitioner shall furnish the requisite number of copies of the petition for the giving of such notices herein required.",-1),h=e("p",null,"Code 1950, § 58-1123; 1978, c. 457; 1984, c. 675.",-1),c=[r,s,h];function d(f,l,p,m,u,_){return i(),o("div",null,c)}const g=t(n,[["render",d]]);export{b as __pageData,g as default};
