import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 58.1-205 Effect of regulations, rulings, etc., and administrative interpretations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-205.md","filePath":"vacode/58.1-205.md"}'),r={name:"vacode/58.1-205.md"},n=i('<h1 id="§-58-1-205-effect-of-regulations-rulings-etc-and-administrative-interpretations" tabindex="-1">§ 58.1-205 Effect of regulations, rulings, etc., and administrative interpretations. <a class="header-anchor" href="#§-58-1-205-effect-of-regulations-rulings-etc-and-administrative-interpretations" aria-label="Permalink to &quot;§ 58.1-205 Effect of regulations, rulings, etc., and administrative interpretations.&quot;">​</a></h1><p>In any proceeding relating to the interpretation or enforcement of the tax laws of this Commonwealth, the following rules shall apply:</p><ol><li><p>Any assessment of a tax by the Department shall be deemed prima facie correct.</p></li><li><p>Any regulation promulgated as provided by subsection B of § <a href="http://law.lis.virginia.gov/vacode/58.1-203/" target="_blank" rel="noreferrer">58.1-203</a> shall be sustained unless unreasonable or plainly inconsistent with applicable provisions of law.</p></li><li><p>Rulings issued in conformity with § <a href="http://law.lis.virginia.gov/vacode/58.1-203/" target="_blank" rel="noreferrer">58.1-203</a>, tax bulletins, guidelines, and other documents published as provided in § <a href="http://law.lis.virginia.gov/vacode/58.1-204/" target="_blank" rel="noreferrer">58.1-204</a>, and guidance documents listed in the Virginia Register of Regulations as provided in § <a href="http://law.lis.virginia.gov/vacode/2.2-4103.1/" target="_blank" rel="noreferrer">2.2-4103.1</a> shall be accorded judicial notice.</p></li><li><p>In any proceeding commenced under § <a href="http://law.lis.virginia.gov/vacode/58.1-1821/" target="_blank" rel="noreferrer">58.1-1821</a>, <a href="http://law.lis.virginia.gov/vacode/58.1-1824/" target="_blank" rel="noreferrer">58.1-1824</a> or <a href="http://law.lis.virginia.gov/vacode/58.1-1825/" target="_blank" rel="noreferrer">58.1-1825</a>, rulings and administrative interpretations other than those described in subdivisions 2 and 3 shall not be admitted into evidence and shall be accorded no weight, except that an assessment made pursuant to any such ruling or interpretation shall be entitled to the presumption of correctness specified in subdivision 1.</p></li></ol><p>Code 1950, § 58-48.8; 1980, c. 633; 1984, c. 675; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0800" target="_blank" rel="noreferrer">800</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0488" target="_blank" rel="noreferrer">488</a>.</p>',4),o=[n];function l(s,d,c,p,g,f){return a(),t("div",null,o)}const v=e(r,[["render",l]]);export{u as __pageData,v as default};
