import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 58.1-2254 Exempt sale deduction.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-2254.md","filePath":"vacode/58.1-2254.md"}'),o={name:"vacode/58.1-2254.md"},i=r('<h1 id="§-58-1-2254-exempt-sale-deduction" tabindex="-1">§ 58.1-2254 Exempt sale deduction. <a class="header-anchor" href="#§-58-1-2254-exempt-sale-deduction" aria-label="Permalink to &quot;§ 58.1-2254 Exempt sale deduction.&quot;">​</a></h1><p>A licensed retailer of alternative fuel who has posted a bond in accordance with § <a href="http://law.lis.virginia.gov/vacode/58.1-2246/" target="_blank" rel="noreferrer">58.1-2246</a> may deduct from the amount of tax otherwise payable to a provider of alternative fuel the amount calculated on alternative fuel that the licensee received from the provider and resold to a governmental entity, or resold to an organization described in subdivision 2 of § <a href="http://law.lis.virginia.gov/vacode/58.1-2250/" target="_blank" rel="noreferrer">58.1-2250</a> for use in the operation of an aircraft, whose purchases of alternative fuel are exempt from the tax under such section if, when purchasing the fuel, the retailer notified the provider of the retailer&#39;s intent to resell the fuel in an exempt sale.</p><p>2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0729" target="_blank" rel="noreferrer">729</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0758" target="_blank" rel="noreferrer">758</a>.</p>',3),n=[i];function l(s,c,d,h,f,p){return t(),a("div",null,n)}const v=e(o,[["render",l]]);export{u as __pageData,v as default};
