import{_ as e,o as i,c as a,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 58.1-2265 Improper sale or use of untaxed fuel; civil penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-2265.md","filePath":"vacode/58.1-2265.md"}'),r={name:"vacode/58.1-2265.md"},o=t('<h1 id="§-58-1-2265-improper-sale-or-use-of-untaxed-fuel-civil-penalty" tabindex="-1">§ 58.1-2265 Improper sale or use of untaxed fuel; civil penalty. <a class="header-anchor" href="#§-58-1-2265-improper-sale-or-use-of-untaxed-fuel-civil-penalty" aria-label="Permalink to &quot;§ 58.1-2265 Improper sale or use of untaxed fuel; civil penalty.&quot;">​</a></h1><p>A. Any person committing any of the following acts shall be subject to the civil penalty specified in subsection B:</p><ol><li><p>Selling or storing any dyed diesel fuel for use in a highway vehicle that is licensed or required to be licensed, unless that use is allowed under 26 U.S.C. § 4082;</p></li><li><p>Willfully altering or attempting to alter the strength or composition of any dye or marker in any dyed diesel fuel;</p></li><li><p>Using dyed diesel fuel in a highway vehicle unless that use is allowed under 26 U.S.C. § 4082;</p></li><li><p>Acquiring, selling or storing any fuel for use in a watercraft, aircraft, or highway vehicle that is licensed or required to be licensed unless the tax levied by this chapter has been paid; or</p></li><li><p>Using any fuel in a watercraft, aircraft, or highway vehicle that is licensed or required to be licensed unless the tax levied by this chapter has been paid.</p></li></ol><p>B. The amount of the civil penalty for any act described in subsection A shall be the greater of $1,000 or ten dollars per gallon of fuel, based on the maximum storage capacity of the storage tank, container or storage tank of the highway vehicle, watercraft or aircraft.</p><p>C. The Commissioner is authorized to reduce or waive any civil penalties under this section if the violation is due to a reasonable or good cause shown to the satisfaction of the Commissioner.</p><p>2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0729" target="_blank" rel="noreferrer">729</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0758" target="_blank" rel="noreferrer">758</a>.</p>',6),l=[o];function n(s,c,h,d,p,u){return i(),a("div",null,l)}const _=e(r,[["render",n]]);export{g as __pageData,_ as default};
