import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 58.1-2267 Refusal to allow inspection or taking of fuel sample; civil penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-2267.md","filePath":"vacode/58.1-2267.md"}'),l={name:"vacode/58.1-2267.md"},r=o('<h1 id="§-58-1-2267-refusal-to-allow-inspection-or-taking-of-fuel-sample-civil-penalty" tabindex="-1">§ 58.1-2267 Refusal to allow inspection or taking of fuel sample; civil penalty. <a class="header-anchor" href="#§-58-1-2267-refusal-to-allow-inspection-or-taking-of-fuel-sample-civil-penalty" aria-label="Permalink to &quot;§ 58.1-2267 Refusal to allow inspection or taking of fuel sample; civil penalty.&quot;">​</a></h1><p>Any person who refuses to allow an inspection or allow the taking of a fuel sample authorized by § <a href="http://law.lis.virginia.gov/vacode/58.1-2276/" target="_blank" rel="noreferrer">58.1-2276</a> or § <a href="http://law.lis.virginia.gov/vacode/58.1-2277/" target="_blank" rel="noreferrer">58.1-2277</a> shall be subject to a civil penalty of $5,000 for each refusal. If the refusal is for a sample to be taken from a vehicle, the penalty shall be payable by the person in whose name the vehicle is registered. If the refusal is for a sample to be taken from any other storage tank or container, the penalty shall be payable by the owner of such storage tank or container.</p><p>2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0729" target="_blank" rel="noreferrer">729</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0758" target="_blank" rel="noreferrer">758</a>.</p>',3),i=[r];function n(s,c,f,p,h,_){return a(),t("div",null,i)}const d=e(l,[["render",n]]);export{g as __pageData,d as default};
