import{_ as e,o as i,c as a,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 58.1-2272 Prohibited acts; criminal penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-2272.md","filePath":"vacode/58.1-2272.md"}'),r={name:"vacode/58.1-2272.md"},o=t('<h1 id="§-58-1-2272-prohibited-acts-criminal-penalties" tabindex="-1">§ 58.1-2272 Prohibited acts; criminal penalties. <a class="header-anchor" href="#§-58-1-2272-prohibited-acts-criminal-penalties" aria-label="Permalink to &quot;§ 58.1-2272 Prohibited acts; criminal penalties.&quot;">​</a></h1><p>A. Any person who commits any of the following acts shall be guilty of a Class 1 misdemeanor:</p><ol><li><p>Failing to obtain a license required by this chapter;</p></li><li><p>Failing to file a return required by this chapter;</p></li><li><p>Failing to pay a tax when due under this chapter;</p></li><li><p>Failing to pay a tax collected on behalf of a destination state to that state when it is due;</p></li><li><p>Making a false statement in an application, return, ticket, invoice, statement, or any other document required under this chapter;</p></li><li><p>Making a false statement in an application for a refund;</p></li><li><p>Failing to keep records as required under this chapter;</p></li><li><p>Refusing to allow the Commissioner or a representative of the Commissioner to examine the person&#39;s books and records concerning fuel;</p></li><li><p>Failing to make a required disclosure of the correct amount of fuel sold or used in the Commonwealth;</p></li><li><p>Failing to file a replacement or additional bond or certificate of deposit as required under this chapter;</p></li><li><p>Failing to show or give a shipping document as required under this chapter;</p></li><li><p>Refusing to allow a licensed distributor, licensed exporter, or licensed importer to defer payment of tax to the supplier, as required by § <a href="http://law.lis.virginia.gov/vacode/58.1-2231/" target="_blank" rel="noreferrer">58.1-2231</a>;</p></li><li><p>Refusing to allow a bulk user of alternative fuel or a retailer of alternative fuel who has posted a bond in accordance with § <a href="http://law.lis.virginia.gov/vacode/58.1-2246/" target="_blank" rel="noreferrer">58.1-2246</a> to defer payment of tax to the provider of alternative fuel, as required by § <a href="http://law.lis.virginia.gov/vacode/58.1-2252/" target="_blank" rel="noreferrer">58.1-2252</a>;</p></li><li><p>Refusing to allow a licensed distributor or a licensed importer to take a deduction or discount allowed by § <a href="http://law.lis.virginia.gov/vacode/58.1-2233/" target="_blank" rel="noreferrer">58.1-2233</a> when remitting the tax to the supplier, or to allow a licensed retailer of alternative fuel to take a deduction or discount allowed by § <a href="http://law.lis.virginia.gov/vacode/58.1-2254/" target="_blank" rel="noreferrer">58.1-2254</a> when remitting the tax to the provider of alternative fuel;</p></li><li><p>Using, delivering, or selling any aviation fuel for use or intended for use in highway vehicles or watercraft;</p></li><li><p>Violating the provisions of § <a href="http://law.lis.virginia.gov/vacode/58.1-2278/" target="_blank" rel="noreferrer">58.1-2278</a>;</p></li><li><p>Interfering with or refusing to permit seizures authorized under § <a href="http://law.lis.virginia.gov/vacode/58.1-2274/" target="_blank" rel="noreferrer">58.1-2274</a>; or</p></li><li><p>Delivering fuel from a transport truck or tank wagon to the fuel tank of a highway vehicle, except in an emergency.</p></li></ol><p>B. A person who knowingly commits any of the following acts shall be guilty of a Class 1 misdemeanor:</p><ol><li><p>Dispenses any fuel on which tax levied pursuant to this chapter has not been paid into the supply tank of a highway vehicle, watercraft, or aircraft; or</p></li><li><p>Allows any fuel on which tax levied pursuant to this chapter has not been paid to be dispensed into the supply tank of a highway vehicle, watercraft, or aircraft.</p></li></ol><p>2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0729" target="_blank" rel="noreferrer">729</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0758" target="_blank" rel="noreferrer">758</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0594" target="_blank" rel="noreferrer">594</a>.</p>',6),l=[o];function n(s,p,h,c,d,f){return i(),a("div",null,l)}const v=e(r,[["render",n]]);export{g as __pageData,v as default};
