import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 58.1-2274 Unlawful importing, transportation, delivery, storage, acquiring or sale of fuel; sale to enforce assessment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-2274.md","filePath":"vacode/58.1-2274.md"}'),o={name:"vacode/58.1-2274.md"},i=r('<h1 id="§-58-1-2274-unlawful-importing-transportation-delivery-storage-acquiring-or-sale-of-fuel-sale-to-enforce-assessment" tabindex="-1">§ 58.1-2274 Unlawful importing, transportation, delivery, storage, acquiring or sale of fuel; sale to enforce assessment. <a class="header-anchor" href="#§-58-1-2274-unlawful-importing-transportation-delivery-storage-acquiring-or-sale-of-fuel-sale-to-enforce-assessment" aria-label="Permalink to &quot;§ 58.1-2274 Unlawful importing, transportation, delivery, storage, acquiring or sale of fuel; sale to enforce assessment.&quot;">​</a></h1><p>A. Upon the discovery of any fuel illegally imported into, or illegally transported, delivered, stored, acquired, or sold in, the Commonwealth, the Commissioner may order the tank or other storage receptacle in which the fuel is located to be seized and locked or sealed until the tax, penalties and interest levied under this chapter are assessed and paid.</p><p>B. If the assessment for such tax is not paid within 30 days, the Commissioner is hereby authorized, in addition to the other remedies authorized in this chapter, to sell such fuel and use the proceeds of such sale to satisfy the assessment due, with any funds which exceed the assessment and costs of the sale being returned to the owner of the fuel.</p><p>C. All fuel and any property, tangible or intangible, which may be found upon the person or in any vehicle which such person is using, including the vehicle itself, to aid the person in the transportation or sale of illegally transported, delivered, stored, sold, imported or acquired fuel, and any property found in the immediate vicinity of any place where such illegally transported, delivered, stored, sold, imported or acquired fuel may be located, including motor vehicles, tanks, and other storage devices, used to aid in the illegal transportation or sale of such fuel, shall be deemed contraband and shall be forfeited to the Commonwealth.</p><p>D. Any efforts by the Department to effect the forfeiture allowed under the authority of this section shall be governed by Chapter 22.1 (§ <a href="http://law.lis.virginia.gov/vacode/19.2-386.1/" target="_blank" rel="noreferrer">19.2-386.1</a> et seq.) of Title 19.2, mutatis mutandis. However, such procedures shall not be applicable to the Department&#39;s tax collection powers and the use of such powers to enforce a tax liability against the illegally transported, delivered, stored, sold, imported or acquired fuel.</p><p>2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0729" target="_blank" rel="noreferrer">729</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0758" target="_blank" rel="noreferrer">758</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0283" target="_blank" rel="noreferrer">283</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0363" target="_blank" rel="noreferrer">363</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0756" target="_blank" rel="noreferrer">756</a>.</p>',6),s=[i];function n(l,d,h,c,f,p){return t(),a("div",null,s)}const m=e(o,[["render",n]]);export{g as __pageData,m as default};
