import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 58.1-2279 Marking requirements for dyed diesel fuel storage facilities.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-2279.md","filePath":"vacode/58.1-2279.md"}'),r={name:"vacode/58.1-2279.md"},s=i('<h1 id="§-58-1-2279-marking-requirements-for-dyed-diesel-fuel-storage-facilities" tabindex="-1">§ 58.1-2279 Marking requirements for dyed diesel fuel storage facilities. <a class="header-anchor" href="#§-58-1-2279-marking-requirements-for-dyed-diesel-fuel-storage-facilities" aria-label="Permalink to &quot;§ 58.1-2279 Marking requirements for dyed diesel fuel storage facilities.&quot;">​</a></h1><p>A. A person who is a retailer of dyed diesel fuel or who stores dyed diesel fuel for use by that person or another person shall mark, with the phrase &quot;Dyed Diesel Fuel, Nontaxable Use Only, Penalty for Taxable Use,&quot; or a similar phrase that clearly indicates that the diesel fuel is not to be used to operate a highway vehicle, each storage facility or pump from which dyed diesel fuel is dispensed, as follows:</p><ol><li><p>The storage tank of the storage facility, if the storage tank is visible; and</p></li><li><p>The dispensing device that serves the storage facility.</p></li></ol><p>B. The marking requirements of this section shall not apply to a storage facility that contains fuel used only in a heating, crop-drying, or manufacturing process, and is installed in a manner that makes use of the fuel for any other purpose improbable.</p><p>2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0729" target="_blank" rel="noreferrer">729</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0758" target="_blank" rel="noreferrer">758</a>.</p>',5),o=[s];function l(n,d,f,c,h,p){return a(),t("div",null,o)}const _=e(r,[["render",l]]);export{g as __pageData,_ as default};
