import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 58.1-2294 Disclosure of information; penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-2294.md","filePath":"vacode/58.1-2294.md"}'),a={name:"vacode/58.1-2294.md"},r=i('<h1 id="§-58-1-2294-disclosure-of-information-penalties" tabindex="-1">§ 58.1-2294 Disclosure of information; penalties. <a class="header-anchor" href="#§-58-1-2294-disclosure-of-information-penalties" aria-label="Permalink to &quot;§ 58.1-2294 Disclosure of information; penalties.&quot;">​</a></h1><p>A. The Commissioner may divulge tax information collected in administering this chapter to the Tax Commissioner, or to any director of finance or other authorized collector of county, city, or town taxes who, for the performance of his official duties, requests the same in writing setting forth the reasons for such request. The Commissioner may also divulge to the executive directors of the Northern Virginia Transportation Commission and the Potomac and Rappahannock Transportation Commission for their confidential use such tax information as may be necessary to facilitate the collection of the taxes levied under this chapter.</p><p>B. Any person to whom tax information is divulged pursuant to this section shall be subject to the prohibitions and penalties prescribed in § <a href="http://law.lis.virginia.gov/vacode/58.1-3/" target="_blank" rel="noreferrer">58.1-3</a> as though that person were a tax official as defined in that section.</p><p>2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0217" target="_blank" rel="noreferrer">217</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0225" target="_blank" rel="noreferrer">225</a>.</p>',4),n=[r];function s(c,l,h,f,d,p){return t(),o("div",null,n)}const u=e(a,[["render",s]]);export{_ as __pageData,u as default};
