import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 58.1-2299.6 Late filing or payment; civil penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-2299.6.md","filePath":"vacode/58.1-2299.6.md"}'),o={name:"vacode/58.1-2299.6.md"},n=i('<h1 id="§-58-1-2299-6-late-filing-or-payment-civil-penalty" tabindex="-1">§ 58.1-2299.6 Late filing or payment; civil penalty. <a class="header-anchor" href="#§-58-1-2299-6-late-filing-or-payment-civil-penalty" aria-label="Permalink to &quot;§ 58.1-2299.6 Late filing or payment; civil penalty.&quot;">​</a></h1><p>A. Any person who fails to file a return required by this chapter on a timely basis shall be subject to a civil penalty. The amount of the civil penalty shall be as follows:</p><ol><li><p>$50 for the first violation;</p></li><li><p>$200 for the second violation;</p></li><li><p>$500 for the third violation; and</p></li><li><p>$1,000 for the fourth and subsequent violations.</p></li></ol><p>After imposition of the penalty under this subsection, the amount of the penalty, if not paid within 30 days of receipt of notice of such penalty, shall bear interest at the rate of one percent per month or fraction thereof until the penalty has been paid.</p><p>B. Interest at the rate of one percent per month or fraction thereof shall accrue on the amount of any taxes due under this chapter that have not been paid to the Commissioner on a timely basis. Such interest shall continue to accrue until such taxes have been paid.</p><p>Any person who fails to pay the Commissioner on a timely basis the amount of taxes due under this chapter shall also be subject to a civil penalty. The amount of the civil penalty shall be equal to 10 percent of the tax due or $50, whichever is greater; however, penalties resulting from an audit shall be equal to 10 percent of the tax due.</p><p>After imposition of the civil penalty under this subsection, the amount of the penalty, if not paid within 30 days of receipt of notice of such penalty, shall bear interest at the rate of one percent per month until both tax and penalty have been paid.</p><p>C. The Commissioner is authorized to reduce or waive any penalties under this section if the violation is due to a reasonable or good cause shown to the satisfaction of the Commissioner.</p><p>2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0217" target="_blank" rel="noreferrer">217</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0225" target="_blank" rel="noreferrer">225</a>.</p>',9),r=[n];function l(s,h,p,c,f,u){return t(),a("div",null,r)}const y=e(o,[["render",l]]);export{m as __pageData,y as default};
