import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 58.1-2501.1 Premium tax; travel insurance.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-2501.1.md","filePath":"vacode/58.1-2501.1.md"}'),i={name:"vacode/58.1-2501.1.md"},o=t('<h1 id="§-58-1-2501-1-premium-tax-travel-insurance" tabindex="-1">§ 58.1-2501.1 Premium tax; travel insurance. <a class="header-anchor" href="#§-58-1-2501-1-premium-tax-travel-insurance" aria-label="Permalink to &quot;§ 58.1-2501.1 Premium tax; travel insurance.&quot;">​</a></h1><p>A. As used in this section:</p><p>&quot;Blanket travel insurance&quot; has the same meaning ascribed thereto in § <a href="/vacode/38.2-1887/">38.2-1887</a>.</p><p>&quot;Primary certificate holder&quot; has the same meaning ascribed thereto in § <a href="/vacode/38.2-1887/">38.2-1887</a>.</p><p>&quot;Primary policyholder&quot; has the same meaning ascribed thereto in § <a href="/vacode/38.2-1887/">38.2-1887</a>.</p><p>&quot;Travel assistance services&quot; has the same meaning ascribed thereto in § <a href="/vacode/38.2-1887/">38.2-1887</a>.</p><p>&quot;Travel insurance&quot; has the same meaning ascribed thereto in § <a href="/vacode/38.2-1887/">38.2-1887</a>.</p><p>B. A travel insurer shall pay premium tax as provided in § <a href="/vacode/58.1-2501/">58.1-2501</a> on travel insurance premiums paid by any of the following:</p><ol><li><p>A primary policyholder who is a resident of the Commonwealth;</p></li><li><p>A primary certificate holder that is a resident of the Commonwealth; or</p></li><li><p>A blanket travel insurance policyholder that is a resident of the Commonwealth or that has its principal place of business or the principal place of business of an affiliate or subsidiary that has purchased blanket travel insurance in the Commonwealth for eligible blanket group members, subject to apportionment rules that apply to the insurer across multiple taxing jurisdictions or that permits the insurer to allocate premiums on an apportioned basis in a reasonable and equitable manner in those jurisdictions.</p></li></ol><p>C. A travel insurer shall (i) document the state of residence or principal place of business of the primary policyholder or primary certificate holder and (ii) report as premium only the amount allocable to travel insurance and not any amounts received for travel assistance services or cancellation fee waivers.</p><p>2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0266" target="_blank" rel="noreferrer">266</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0346" target="_blank" rel="noreferrer">346</a>.</p>',11),n=[o];function s(l,c,p,h,m,u){return a(),r("div",null,n)}const v=e(i,[["render",s]]);export{f as __pageData,v as default};
