import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 58.1-2521 Time for filing declarations of estimated tax.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-2521.md","filePath":"vacode/58.1-2521.md"}'),r={name:"vacode/58.1-2521.md"},i=o('<h1 id="§-58-1-2521-time-for-filing-declarations-of-estimated-tax" tabindex="-1">§ 58.1-2521 Time for filing declarations of estimated tax. <a class="header-anchor" href="#§-58-1-2521-time-for-filing-declarations-of-estimated-tax" aria-label="Permalink to &quot;§ 58.1-2521 Time for filing declarations of estimated tax.&quot;">​</a></h1><p>A. The declaration of estimated tax shall be filed as follows:</p><p>If the requirements of subsection A of § <a href="http://law.lis.virginia.gov/vacode/58.1-2520/" target="_blank" rel="noreferrer">58.1-2520</a> are first met:</p><ol><li><p>Before April 1 of the taxable year, the declaration shall be filed on or before April 15 of the taxable year.</p></li><li><p>After March 31 but before June 1 of the taxable year, the declaration shall be filed on or before June 15 of the taxable year.</p></li><li><p>After May 31, but before September 1 of the taxable year, the declaration shall be filed on or before September 15 of the taxable year.</p></li><li><p>After August 31, but before December 1 of the taxable year, the declaration shall be filed on or before December 15 of the taxable year.</p></li></ol><p>B. The application of this section to taxable years of less than 12 months shall be in accordance with guidelines prescribed by the Department.</p><p>Code 1950, § 58-502.3; 1968, c. 13; 1984, c. 675; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0850" target="_blank" rel="noreferrer">850</a>.</p>',6),l=[i];function f(n,s,c,d,h,p){return a(),t("div",null,l)}const m=e(r,[["render",f]]);export{_ as __pageData,m as default};
