import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 58.1-2526 Where declarations filed and how payments made; refunding overpayments.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-2526.md","filePath":"vacode/58.1-2526.md"}'),n={name:"vacode/58.1-2526.md"},o=r('<h1 id="§-58-1-2526-where-declarations-filed-and-how-payments-made-refunding-overpayments" tabindex="-1">§ 58.1-2526 Where declarations filed and how payments made; refunding overpayments. <a class="header-anchor" href="#§-58-1-2526-where-declarations-filed-and-how-payments-made-refunding-overpayments" aria-label="Permalink to &quot;§ 58.1-2526 Where declarations filed and how payments made; refunding overpayments.&quot;">​</a></h1><p>A. Every insurance company required by this article to file a declaration and make payment of the estimated tax shall file and pay the same with the Department. All such payments shall be deposited by the Department into the state treasury.</p><p>B. If any insurance company overestimates and overpays estimated tax or overpays as a result of increased regulatory costs imposed pursuant to § <a href="http://law.lis.virginia.gov/vacode/38.2-1026/" target="_blank" rel="noreferrer">38.2-1026</a>, the Department shall issue a refund of the amount of the overpayment to the taxpayer. The overpayment shall be refunded out of the state treasury. No interest shall be paid on the refund of any overpayment.</p><p>Code 1950, § 58-502.5; 1968, c. 13; 1984, c. 675; 1985, c. 221; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0571" target="_blank" rel="noreferrer">571</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0850" target="_blank" rel="noreferrer">850</a>; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0029" target="_blank" rel="noreferrer">29</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0163" target="_blank" rel="noreferrer">163</a>.</p>',4),i=[o];function s(l,d,p,h,c,f){return a(),t("div",null,i)}const u=e(n,[["render",s]]);export{_ as __pageData,u as default};
