import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 58.1-2606.1 Local taxation for solar photovoltaic projects five megawatts or less.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-2606.1.md","filePath":"vacode/58.1-2606.1.md"}'),s={name:"vacode/58.1-2606.1.md"},r=o('<h1 id="§-58-1-2606-1-local-taxation-for-solar-photovoltaic-projects-five-megawatts-or-less" tabindex="-1">§ 58.1-2606.1 Local taxation for solar photovoltaic projects five megawatts or less. <a class="header-anchor" href="#§-58-1-2606-1-local-taxation-for-solar-photovoltaic-projects-five-megawatts-or-less" aria-label="Permalink to &quot;§ 58.1-2606.1 Local taxation for solar photovoltaic projects five megawatts or less.&quot;">​</a></h1><p>A. Notwithstanding clause (iv) of subsection C of § <a href="/vacode/58.1-3660/">58.1-3660</a>, generating equipment of solar photovoltaic projects five megawatts or less shall be taxable by a locality, at a rate determined by such locality, but shall not exceed the real estate rate applicable in that locality, and notwithstanding subsection F of § <a href="/vacode/58.1-3660/">58.1-3660</a>, the exemption shall be as follows: 80 percent of the assessed value in the first five years in service after commencement of commercial operation, 70 percent of the assessed value in the second five years in service, and 60 percent of the assessed value for all remaining years in service.</p><p>B. Notwithstanding clause (iii) of subsection B of § <a href="/vacode/58.1-2636/">58.1-2636</a>, solar photovoltaic projects five megawatts or less shall not be exempt from the assessment of a revenue share by ordinance of that locality, as otherwise authorized pursuant to the provisions of § <a href="/vacode/58.1-2636/">58.1-2636</a>. If a locality assesses a revenue share on solar photovoltaic projects five megawatts or less pursuant to this subsection, the exemption for such projects, as measured in alternating current (AC) generation capacity, shall, in lieu of the amounts specified in subsection A, be 100 percent of the assessed value.</p><p>C. Nothing herein shall be construed to authorize local taxation pursuant to this section, § <a href="/vacode/58.1-2636/">58.1-2636</a>, or § <a href="/vacode/58.1-3660/">58.1-3660</a> of generating or storage equipment of solar photovoltaic projects that serve the electricity needs of that property upon which such solar facilities are located, as is provided in § <a href="/vacode/15.2-2288.7/">15.2-2288.7</a>.</p><p>2022, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0492" target="_blank" rel="noreferrer">492</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0493" target="_blank" rel="noreferrer">493</a>.</p>',5),i=[r];function n(c,l,h,f,p,d){return a(),t("div",null,i)}const m=e(s,[["render",n]]);export{u as __pageData,m as default};
