import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 58.1-2626.1 The Virginia Coal Employment and Production Incentive Tax Credit.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-2626.1.md","filePath":"vacode/58.1-2626.1.md"}'),i={name:"vacode/58.1-2626.1.md"},n=o('<h1 id="§-58-1-2626-1-the-virginia-coal-employment-and-production-incentive-tax-credit" tabindex="-1">§ 58.1-2626.1 The Virginia Coal Employment and Production Incentive Tax Credit. <a class="header-anchor" href="#§-58-1-2626-1-the-virginia-coal-employment-and-production-incentive-tax-credit" aria-label="Permalink to &quot;§ 58.1-2626.1 The Virginia Coal Employment and Production Incentive Tax Credit.&quot;">​</a></h1><p>A. For the tax years beginning on and after January 1, 1988, but before January 1, 2022, every corporation in the Commonwealth doing the business of furnishing water, heat, light, or power to the Commonwealth or its citizens, whether by means of electricity, gas, or steam shall be allowed a credit against the tax imposed by § <a href="/vacode/58.1-2626/">58.1-2626</a> in the following amount: $1 per ton for each ton of coal purchased and consumed by such corporation in excess of the number of tons of Virginia coal purchased by such corporation in 1985, provided such coal was mined in Virginia as certified by the producer of such coal. This credit shall be prorated equally against the corporation&#39;s estimated payments made in September and December and the final payment.</p><p>B. For tax years beginning on and after January 1, 1989, but before January 1, 2022, every corporation in the Commonwealth doing the business of furnishing water, heat, light, or power to the Commonwealth or its citizens, whether by means of electricity, gas, or steam shall be allowed additional credit against the tax imposed by § <a href="/vacode/58.1-2626/">58.1-2626</a> in the following amount: $1 per ton for each ton of coal purchased and consumed by such corporation, provided such coal was mined in Virginia as certified by such seller. The credit shall be prorated equally against the corporation&#39;s estimated payments made in September and December and the final payment.</p><p>1986, c. 450; 1989, c. 429; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0929" target="_blank" rel="noreferrer">929</a>; 2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0553" target="_blank" rel="noreferrer">553</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0554" target="_blank" rel="noreferrer">554</a>.</p>',4),r=[n];function c(s,l,d,h,p,m){return a(),t("div",null,r)}const g=e(i,[["render",c]]);export{u as __pageData,g as default};
