import{_ as e,o,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 58.1-2662.1 Gross receipts of telephone and telegraph companies.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/58.1-2662.1.md","filePath":"vacode/58.1-2662.1.md"}'),a={name:"vacode/58.1-2662.1.md"},n=r('<h1 id="§-58-1-2662-1-gross-receipts-of-telephone-and-telegraph-companies" tabindex="-1">§ 58.1-2662.1 Gross receipts of telephone and telegraph companies. <a class="header-anchor" href="#§-58-1-2662-1-gross-receipts-of-telephone-and-telegraph-companies" aria-label="Permalink to &quot;§ 58.1-2662.1 Gross receipts of telephone and telegraph companies.&quot;">​</a></h1><p>The special regulatory revenue tax on telephone and telegraph companies levied pursuant to § <a href="http://law.lis.virginia.gov/vacode/58.1-2660/" target="_blank" rel="noreferrer">58.1-2660</a> shall be based on gross receipts with the following deductions:</p><ol><li><p>Revenue billed on behalf of another such telephone company or person to the extent such revenues are later paid over to or settled with that company or person;</p></li><li><p>Revenues received from a telephone company for providing to the company any of the following: (i) unbundled network facilities; (ii) completion, origination or interconnection of telephone calls with the taxpayer&#39;s network; (iii) transport of telephone calls over the taxpayer&#39;s network; or (iv) taxpayer&#39;s telephone services for resale;</p></li><li><p>Revenue received as the proportionate part of interstate revenue attributable to the Commonwealth;</p></li><li><p>Revenue received from a person providing video programming for the transport of video programming to an end-user subscriber&#39;s premises or for access to a video dialtone network; and</p></li><li><p>Revenue, other than from line charges, received from pay telephone service.</p></li></ol><p>1988, c. 727; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0751" target="_blank" rel="noreferrer">751</a>; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0897" target="_blank" rel="noreferrer">897</a>.</p>',4),i=[n];function s(p,l,c,h,d,f){return o(),t("div",null,i)}const _=e(a,[["render",s]]);export{g as __pageData,_ as default};
